import { getRole, getUserRole, addUserRole } from '@/api/xtgl/role'
import { getOrganization } from '@/api/xtgl/organization'
import { getTree, getTreeNode } from '@/utils/index'
export default {
  name: 'SelectUsers',
  components: {
  },
  props: {
    obj: {
      type: Object,
      default: null
    }
  },
  data() {
    return {
      treeList: null,
      listLoading: true,

      total: 0,
      currentPage: 1,
      pageSize: 10,

      selection: [],
      buttonLoading: false
    }
  },
  mounted() {
    this.fetchData()

    this.listLoading = true
    var postData = {}
    postData.currentPage = this.currentPage
    postData.pageSize = this.pageSize
    getOrganization(postData).then(response => {
      response.data.forEach(ele => {
        ele.label = ele.name
      })
      this.treeList = getTree(0, response.data)
      this.listLoading = false
    })
  },
  methods: {
    fetchData() {
      this.listLoading = true

      var postData = {}
      postData.currentPage = this.currentPage
      postData.pageSize = this.pageSize

      new Promise(resolve => {
        getRole(postData).then(response => {
          this.list = response.data
          resolve(response.data)
        })
      }).then(list => {
        getUserRole({ userId: this.obj.id }).then(response => {
          response.data.forEach(currentRole => {
            list.forEach(element => {
              if (element.id === currentRole.roleId) {
                this.$refs.SelectRolePanel.toggleRowSelection(element)
              }
            })
          })
          this.listLoading = false
        })
      }).catch(() => { this.listLoading = false })
    },
    handleAdd() {
      this.buttonLoading = true
      const roleIds = []
      this.selection.forEach(ele => {
        roleIds.push(ele.id)
      })
      addUserRole({ usersId: this.obj.id, roleIds: roleIds.join() }).then(response => {
        this.$message(response.message)
        this.buttonLoading = false
        this.$emit('dialogClose')
      }).catch(() => { this.buttonLoading = false })
    },
    handleSelectionChange(val) {
      this.selection = val
    },
    handleNodeClick(data, node) {
      // 获取当前点击节点下面所有子节点 id 数组
      var res = getTreeNode(data, [])
      // 添加 当前节点 id
      res.push(data.id)
      // id数组 拼接成字符串
      this.orgId = res.join()
      this.fetchData()
    }
  }
}
