import { putTechnicalBid, addTechnicalBid, getTechnicalBidEditInit, getTechnicalBidById, getBillFlow, deleteTechnicalBid } from '@/api/ywgl/technicalBid'
import UpFile from '@/components/UpLoad/UpFile'
import FlowSelect from '@/components/Flow/flowSelect'
// import SelectProject from '@/components/Other/xmgl/selectProject'
import OrgSelect2 from '@/components/OrgSelect/index2'
export default {
  name: 'ProjectSiteSignEdit',
  components: {
    FlowSelect,
    UpFile,
    OrgSelect2
    // SelectProject
  },
  data() {
    return {
      ruleForm: {
        id: 0,
        number: '',
        orgId: null,
        companyId: this.$store.state.user.selectOrgId,
        name: '',
        remark: '',
        state: 0,
        billFlowId: 0,
        fileList: []
      },
      siteSignId: null,
      projectName: '',

      keyOrg: 0,
      orgList: [],
      coordinateTypeList: [],
      progressList: [],

      attitudePara: null,
      loadFlow: false,
      billFlow: null,
      isCheck: false,

      active: 0,
      loading: false,
      rules: {
        orgId: [{ required: true, message: '请输入', trigger: 'change' }],
        name: [{ required: true, message: '请输入', trigger: 'blur' }],
        reportAmount: [{ required: true, message: '请输入', trigger: 'blur' }],
        approveAmount: [{ required: true, message: '请输入', trigger: 'blur' }],
        costAmount: [{ required: true, message: '请输入', trigger: 'blur' }],
        firstPartyOwner: [{ required: true, message: '请输入', trigger: 'blur' }],
        happenedDate: [{ required: true, message: '请选择', trigger: 'blur' }],
        planCpmpleteDate: [{ required: true, message: '请选择', trigger: 'blur' }],
        coordinateType: [{ required: true, message: '请选择', trigger: 'blur' }],
        progressId: [{ required: true, message: '请选择', trigger: 'blur' }],
        sendDate: [{ required: false, message: '请选择', trigger: 'blur' }],
        transactDate: [{ required: false, message: '请选择', trigger: 'blur' }]
      }
    }
  },
  watch: {
    'ruleForm.progressId': {
      handler: function(value) {
        if (value === 1) { // 已发生
          this.rules.sendDate[0].required = true
          this.rules.transactDate[0].required = false
          this.isCheck = false
        } else if (value === 2) { // 已上报
          this.rules.sendDate[0].required = false
          this.rules.transactDate[0].required = true
          this.isCheck = true
        }
      },
      immediate: true
    }
  },
  activated() {
    if (this.$route.params.id) {
      this.ruleForm.orgId = null // 刷新 org 列表
      this.initData()
      this.refreshRouterName = this.$route.params.refreshRouterName
    }
  },
  mounted() {
    if (!this.$route.params.id) {
      this.initData()
      this.activate = true
    }
  },
  methods: {
    submitForm(formName, state) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          var newObj = JSON.parse(JSON.stringify(this.ruleForm))
          let tempIds = []
          if (newObj.fileList.length > 0) {
            tempIds = newObj.fileList.map(m => {
              return { id: m.id, addFlag: m.addFlag }
            })
          } else {
            if (state === 1) {
              this.$message('必须上传附件!')
              return
            }
          }
          newObj.fileList = tempIds
          newObj.state = state

          if (this.billFlow) {
            // 获取流程组件中的数据
            const billFlow = this.$refs.flowSelect.getBillFlow()
            if (!billFlow) {
              return false
            }
            newObj.billFlow = billFlow
          } else {
            this.$message('流程数据不能为空')
            return
          }
          this.loading = true

          if (this.technicalBidId) {
            putTechnicalBid(newObj).then(response => {
              if (response.errorMsg) {
              // 验证错误
                this.$alert(`${response.errorMsg.replace(/；/g, '；<br />')}`,
                  { dangerouslyUseHTMLString: true })
                return
              }
              this.$message(response.message)
              this.loading = false
              this.closeThePage()
            }).catch(() => {
              this.loading = false
              this.active = 0
            })
          } else {
            addTechnicalBid(newObj).then(response => {
              if (response.errorMsg) {
                // 验证错误
                this.$alert(`${response.errorMsg.replace(/；/g, '；<br />')}`,
                  { dangerouslyUseHTMLString: true })
                return
              }
              this.$message(response.message)
              this.loading = false
              this.closeThePage()
              // this.setData(response)
            }).catch(() => {
              this.loading = false
              this.active = 0
            })
          }
        }
      })
    },
    initData() {
      // 有参数id 就是编辑
      if (this.$route.query.id) {
        this.siteSignId = this.$route.query.id
      }
      new Promise((resolve, reject) => {
        getTechnicalBidEditInit().then(response => {
          const { orgList, result1, result4 } = response
          this.orgList = orgList
          this.coordinateTypeList = result1
          this.progressList = result4
          this.keyOrg += 1
          resolve(orgList)
        })
      }).then(orgList => {
        if (this.siteSignId) {
          getTechnicalBidById({ id: this.siteSignId }).then(response => {
            const { data } = response
            this.ruleForm = data
            this.keyOrg += 1
            this.ruleForm.fileList = data.upFile
          })
        }
      }).catch(() => {
        this.$message.error('信息错误')
      })
    },
    getDynamicFlow(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          var newObj = JSON.parse(JSON.stringify(this.ruleForm))

          getBillFlow(newObj).then(response => {
            if (response.success) {
              this.billFlow = response.data.billFlow
              this.active = 1
            } else {
              this.$message(response.message)
            }
          })
        }
      })
    },
    previous() {
      // 上一步
      this.active--
      this.billFlow = null
    },
    cancel() {
      this.$confirm('作废以后将无法编辑，是否确定作废？', '提示', {
        type: 'warning'
      }).then(() => {
        this.active = 2
        this.loading = true
        deleteTechnicalBid({ id: this.ruleForm.id }).then(response => {
          this.$message(response.message)
          this.loading = false
          this.closeThePage()
        })
      }).catch(() => {})
    },
    closeThePage() {
      this.utils.closeAndRefresh(this.$route.query.refreshRouterName)
    },
    selectProject() {
      this.$refs.projectRef.openDialog()
    },
    getProject() {}
  }
}
