import UserRoleAdd from './userRoleAdd'
import { getTree } from '@/utils/index'
export default {
  name: 'SelectRole',
  components: {
    UserRoleAdd
  },
  props: {
    id: {
      type: Number,
      default: null
    }
  },
  data() {
    return {
      list: [],
