  methods: {
    fetchData() {
      this.listLoading = true
      var postData = {}
      postData.currentPage = this.currentPage
      postData.pageSize = this.pageSize

      getUserRole({ userId: this.$route.query.id }).then(response => {
        const { data } = response
        // this.list = ua
