        this.roleIds = data.map(m => { return m.roleId }).toString()

        this.getUserRolePermit()
        this.listLoading = false
      })
    },
    getUserRolePermit() {
      new Promise((resolve, reject) => {
        GetPageAndPermit().then(response => {
          const { permitData, pageData } = response.data
