import { getProjectOutputAttitudeById } from '@/api/xmgl/projectOutput'
import UpFile from '@/components/UpLoad/UpFile'
import FlowDisplay from '@/components/Flow/flowDisplay'
import FlowAttitude from '@/components/Flow/flowAttitude'
export default {
  name: 'ProjectOutputAttitude',
  components: {
    FlowDisplay,
    FlowAttitude,
    UpFile
  },
  data() {
    return {
      ruleForm: {
        id: 0,
        number: '',
        orgId: null,
        companyId: this.$store.state.user.selectOrgId,
        projectId: null,
        outputYear: null,
        outputMonth: null,
        projectManagerId: null,
        internalOutput: '',
        externalOutput: '',
        receivable: '',
        remark: '',
        state: 0,
        billFlowId: 0,
        fileList: []
      },
      monthList: [{
        value: 1,
        label: '1月'
      }, {
        value: 2,
        label: '2月'
      }, {
        value: 3,
        label: '3月'
      }, {
        value: 4,
        label: '4月'
      }, {
        value: 5,
        label: '5月'
      }, {
        value: 6,
        label: '6月'
      }, {
        value: 7,
        label: '7月'
      }, {
        value: 8,
        label: '8月'
      }, {
        value: 9,
        label: '9月'
      }, {
        value: 10,
        label: '10月'
      }, {
        value: 11,
        label: '11月'
      }, {
        value: 12,
        label: '12月'
      }, {
        value: 0,
        label: '年度调节'
      }],
      projectOutputId: null,
      orgName: '',

      attitudePara: null,
      isLoad: false,
      billFlow: null,

      rules: {
      }
    }
  },
  activated() {
    if (this.$route.params.id) {
      this.initData()
      this.refreshRouterName = this.$route.params.refreshRouterName
    }
  },
  mounted() {
    if (!this.$route.params.id) {
      this.initData()
    }
  },
  methods: {
    initData() {
      // 有参数id
      if (this.$route.query.id) {
        this.projectOutputId = this.$route.query.id
      }
      if (this.projectOutputId) {
        this.loading = true
        getProjectOutputAttitudeById({ id: this.projectOutputId }).then(response => {
          const { data, billFlowData, orgName } = response
          this.ruleForm = data
          this.orgName = orgName
          this.ruleForm.fileList = data.upFile
          this.billFlow = billFlowData
          this.attitudePara = { formId: billFlowData.formId, billFlowId: data.billFlowId, planId: data.id }
          this.isLoad = true // 加载审批组件
        })
      }
    },
    /**
     * @description: 单据状态
     * @param {Number} val
     */
    getFormState(val) {
      if (!this.billFlow) {
        return '流程空'
      }
      const formState = this.billFlow.form.formState
      const state = val
      const obj = formState.find(f => f.value === state)
      if (obj) { return obj.name } else { return '状态不详' }
    },
    closeThePage() {
      this.utils.closeAndRefresh(this.$route.query.refreshRouterName)
    },
    updateFlow() {
      this.closeThePage()
    }
  }
}
