import { getWorkerPausePaging } from '@/api/ggzx/workerPause'
import { getDefaultOptions } from '@/api/ggzx/worker'
import SearchBar from '@/components/SearchBar'
import searchData from './searchData'
export default {
  name: 'WorkerPause',
  components: {
    SearchBar
  },
  data() {
    return {
      size: 'mini',
      searchData: searchData, // 渲染搜索栏数据
      filter: { // 过滤数据
        type: '3', // 类型
        typeOptions: [ // 类型数组
          {
            value: '0',
            name: '我发起的'
          },
          {
            value: '1',
            name: '待审批'
          },
          {
            value: '2',
            name: '已审批'
          },
          {
            value: '3',
            name: '列表'
          }
        ]
      },
      /* 列表数据相关 */
      table: {
        list: [],
        loading: false,
        total: 0,
        currentPage: 1,
        pageSize: 10
      }
    }
  },
  beforeRouteEnter(to, from, next) {
    next(vm => {
      vm.fetchData()
      vm.getOptions()
    })
  },
  methods: {
    /**
     * 类型重新选择的事件
     */
    typeChange() {
      this.fetchData()
    },
    getOptions() {
      getDefaultOptions().then(op => {
        const { yearOptions } = op
        // 搜索栏数据初始化
        this.searchData = this.searchData.map(item => {
          item.options =
            item.name === 'year' ? yearOptions : item.options
          return item
        })
      })
    },
    /**
     * @description: 获取列表数据、搜索数据
     * @return {*}
     */
    async fetchData() {
      this.table.loading = true
      try {
        // 获取请求参数
        const postData = this.getSearchData()
        const { currentPage, pageSize } = this.table
        // 请求开始
        const response = await getWorkerPausePaging({
          ...postData,
          currentPage,
          pageSize,
          type: this.filter.type
        })
        const { data, count } = response

        // 列表数据初始化
        this.table.list = data
        this.table.total = count
      } catch (e) {
        console.log('fetchData:请求列表数据失败，错误信息：', e)
      } finally {
        this.table.loading = false
      }
    },
    /**
     * @description: 搜索栏数据转换
     * @return {Object} request数据
     */
    getSearchData() {
      const lastSearch = {}
      this.searchData.forEach(item => {
        const { value, transformType, name } = item
        if (transformType === 'property-serial') {
          value.forEach((ele, index) => {
            const serial = index + 1
            lastSearch[name + serial] = ele
          })
        } else {
          lastSearch[name] = value
        }
      })
      return lastSearch
    },
    /**
     * @description: 设置分页大小
     * @param {Number} val
     */
    sizeChange(val) {
      this.table.pageSize = val
      this.fetchData()
    },
    /**
     * @description: 搜索回调
     */
    handelSearch() {
      this.table.currentPage = 1 // 返回第一页
      this.fetchData()
    },
    /**
     * @description: 路由到详情页
     * @param {*} row
     */
    handleDetails(row) {
      this.$router.push({ name: 'WorkerPauseDetail', query: { id: row.id, refreshRouterName: this.$route.name }})
    },
    /**
     * @description: table编辑
     * @param {Object} id
     */
    handleEdit(id) {
      this.$router.push({ name: 'WorkerPauseEdit', query: { id, refreshRouterName: this.$route.name }})
    },
    getFormState(row) {
      if (!row.billFlow) { return '流程空' }
      const formState = row.billFlow.form.formState
      this.formState = formState
      const state = row.state
      if (state === -1) return '作废'
      const obj = formState.find(f => f.value === state)
      if (obj) { return obj.name } else { return '状态不详' }
    },
    getPerson(bf) {
      if (bf) {
        var arr = bf.billFlowNode.filter(f => f.isCurrentState && !f.isChecked)
        return arr.map(m => m.person.name).join('、')
      }
    },
    // 审批
    attitude(id) {
      this.$router.push({ name: 'WorkerPauseAttitude', query: { id, refreshRouterName: this.$route.name }})
    }
  }
}
