import { getAllMaterialProperty, deleteMaterialProperty } from '@/api/cggl/material'
import AddMaterialProperty from './components/addMaterialProperty'
export default {
  name: 'MaterialProperty',
  components: {
    AddMaterialProperty
  },
  data() {
    return {
      list: [],
      listLoading: false,
      dialogTableVisible: false,
      dialogTitle: '物料属性编辑',

      currentRow: null,
      buttonLoading: false,

      organizationId: this.$store.state.user.selectOrgId
    }
  },
  mounted() {
    this.fetchData()
  },
  methods: {
    fetchData() {
      this.getPorperty()
    },
    getPorperty() {
      this.listLoading = true
      var postData = {}
      postData.organizationId = this.organizationId
      getAllMaterialProperty(postData).then(response => {
        this.list = response.data
        this.listLoading = false
      }).catch(() => { this.listLoading = false })
    },
    dialogClose() {
      this.dialogTableVisible = false
      // this.fetchData()
      this.getPorperty()
    },
    handleAdd() {
      this.currentRow = null
      // this.organizationId = 0
      this.dialogTableVisible = true
    },
    handleEdit(row) {
      this.currentRow = row
      this.dialogTableVisible = true
    },
    handleDel(id) {
      this.$confirm('是否确定删除?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        deleteMaterialProperty({ id: id }).then(response => {
          this.getPorperty()
          this.$message.success(response.message)
        })
      }).catch(() => {
        this.$message.info('已经取消删除')
      })
    }
  }
}
