import { getProjectLXPaging } from '@/api/xmgl/project'
import OrgSelect2 from '@/components/OrgSelect/index2'
export default {
  name: 'ProjectLX',
  components: {
    OrgSelect2
  },
  data() {
    return {
      list: [],
      listLoading: false,

      total: 0,
      currentPage: 1,
      pageSize: 10,
      search: {
        name: '',
        orgIds: [],
        number: '',
        states: [],
        type: 0
      },
      tabPosition: 0,
      orgList: null,
      formState: null
    }
  },
  mounted() {
    this.fetchData()
  },
  methods: {
    fetchData() {
      this.listLoading = true
      var postData = {}
      postData = this.search
      postData.currentPage = this.currentPage
      postData.pageSize = this.pageSize
      postData.name = this.searchText
      getProjectLXPaging(postData).then(response => {
        const { data, count, orgList } = response
        this.orgList = orgList
        this.list = data
        this.total = count
        this.listLoading = false
      })
    },
    sizeChange(val) {
      this.pageSize = val
      this.fetchData()
    },
    radioChange(value) {
      // 清空和切换条件
      this.search = {
        orgIds: [],
        name: '',
        number: '',
        states: [],
        type: value
      }
      // 填充数据
      this.fetchData()
    },
    getFormState(row) {
      if (!row.billFlow) { return '流程空' }
      const formState = row.billFlow.form.formState
      this.formState = formState
      const state = row.state
      if (state === -1) return '作废'
      const obj = formState.find(f => f.value === state)
      if (obj) { return obj.name } else { return '状态不详' }
    },
    getPerson(bfn) {
      if (bfn) {
        var arr = bfn.filter(f => f.isCurrentState && !f.isChecked)
        return arr.map(m => m.person.name).join('、')
      }
    },
    handleAdd() {
      this.$router.push({ name: 'ProjectSiteSignEdit', query: { refreshRouterName: this.$route.name }})
    },
    handleExport() {},
    handleEdit(row) {
      this.$router.push({ name: 'ProjectSiteSignEdit', query: { id: row.id, refreshRouterName: this.$route.name }, params: { id: row.id, type: 3 }})
    },
    attitude(row) {
      this.$router.push({ name: 'ProjectSiteSignAttitude', query: { id: row.id, refreshRouterName: this.$route.name }, params: { id: row.id }})
    },
    handleDetail(row) {
      this.$router.push({ name: 'ProjectSiteSignDetail', query: { id: row.id, refreshRouterName: this.$route.name }, params: { id: row.id }})
    },
    goSearch() {
      this.fetchData()
    }
  }
}
