import TableEx from '@/components/TableEx'
import { GetProjectSiteSignPaging } from '@/api/xmgl/ProjectSiteSign.js'
import SearchBar from '@/components/SearchBar'
// import searchData from './searchData'
export default {
  name: 'ProjectSiteSignIndex',
  components: {
    SearchBar,
    TableEx
  },
  data() {
    return {
      size: 'mini', // 整体大小 medium / small / mini
      searchData: require('./searchData').default(), // 渲染搜索栏数据
      lastSearch: null, // 最近一次搜索的数据
      approveAmount: 0, // 审定金额
      costAmount: 0, // 成本金额
      reportAmount: 0, // 上报金额

      filter: { // 过滤数据
        type: 3,
        types: [
          { label: '我发起的', value: 0 },
          { label: '待审批', value: 1 },
          { label: '已审批', value: 2 },
          { label: '列表', value: 3 }
        ]
      },

      /* 列表数据相关 */
      table: {
        // columns注释在 @/components/TableEx/index.vue
        columns: [
          {
            label: '编号', // 表头
            prop: 'number', // 渲染的属性
            class: 'link', // class
            width: '150', // 最小宽度
            click: (row) => {
              this.handleDetails(row)
            }
          },
          {
            label: '项目',
            prop: 'projectName',
            width: '130'
          },
          {
            label: '组织机构',
            prop: 'orgName',
            width: '130'
          },
          {
            label: '名称',
            prop: 'name',
            width: '130'
          },
          {
            label: '指令日期',
            prop: 'occurDate',
            format: 'date',
            width: '100'
          },
          {
            label: '上报值(元)',
            prop: 'reportAmount',
            format: 'money',
            width: '100',
            align: 'right'
          },
          {
            label: '审定值(元)',
            prop: 'approveAmount',
            format: 'money',
            width: '100',
            align: 'right'
          },
          {
            label: '成本值(元)',
            prop: 'costAmount',
            format: 'money',
            width: '100',
            align: 'right'
          },
          {
            label: '利润率',
            prop: 'rate',
            width: '80',
            format: (val) => val + '%'
          },
          {
            label: '进度',
            prop: 'progressType',
            width: '100'
          },
          {
            label: '完成日期',
            format: 'date',
            prop: 'predictFinishDate',
            width: '100'
          },
          {
            label: '剩余时间',
            prop: 'sysj',
            width: '80'
          },
          {
            label: '项目状态',
            prop: 'projectState',
            width: '100'
          },
          {
            label: '协助',
            prop: 'coordinateType',
            width: '100'
          },
          {
            label: '待处理',
            prop: 'pendingPerson',
            format: (val, format, row, column) => {
              return Array.isArray(val) ? val.join('、') : val
            },
            width: '100'
          },
          {
            business: 'createUser' // 业务部分的快捷使用参考 src\components\TableEx\Column\businessList.js
          },
          {
            business: 'state' // 业务部分的快捷使用参考 src\components\TableEx\Column\businessList.js
          }
        ],
        list: [],
        exLoading: false, // 导出loading
        tbLoading: false, // table loading
        total: 0,
        currentPage: 1,
        pageSize: this.utils.getPersonalSettings().pageSize
      }
    }
  },
  /**
   * 最后决定使用beforeRouteEnter路由守卫代替activated,和created;
   * 其好处是:无论用哪种方式进入都会触发,并且只触发一次;
   */
  beforeRouteEnter(to, from, next) {
    next(vm => {
      vm.fetchData()
    })
  },
  created() {
    console.log(this.utils.hasPower('XMGLPROJECTSITESIGN_UPDATE'))
  },
  methods: {
    fetchData(type) { // SearchBar 事件类型 重置:reset  搜索:search
      switch (type) {
        case 'filter':
          this.table.currentPage = 1
          break
        case 'reset':
        case 'search':
          this.table.currentPage = 1
          this.lastSearch = null
          break
      }
      this.table.tbLoading = true
      this.getTable()
        .then(res => this.parseServeDate(res))
        .finally(() => {
          this.table.tbLoading = false
        })
    },
    /**
     * @description: table 行的 className 的回调方法，也可以使用字符串为所有行设置一个固定的 className。
     * @param {*} row 当前行数据
     * @param {*} rowIndex 当前行index
     * @return {*}
     */
    rowClassName({ row, rowIndex }) {
      const { sysj, approveAmount, costAmount, state } = row
      if (state === 0) return ''
      if (approveAmount < costAmount) {
        return 'color-purple'
      } else if (Number(sysj) < 0) {
        return 'color-red'
      }
      return ''
    },
    /**
     * @description:表格导出功能
     */
    async exportExcel() {
      const Excel = this.$refs['TableEx'].initExcel()
      this.getTable(1, this.table.total).then(({ data }) => {
        Excel.format([{ ...Excel.sheet[0], table: data }]).export()
      }).catch(() => {
        this.$message.error('导出失败')
        Excel.cancel()
      })
    },
    parseServeDate(res) {
      const {
        data,
        count,
        coordinateTypeList,
        progressTypeList,
        projectStateList,
        warningStateList,
        approveAmount,
        costAmount,
        reportAmount
      } = res

      this.approveAmount = approveAmount
      this.costAmount = costAmount
      this.reportAmount = reportAmount
      // 搜索栏数据初始化
      const tranOptions = (ele) => {
        return { ...ele, label: ele.name }
      }
      // const all = { value: '', name: '全部' }
      // coordinateTypeList.unshift(all)
      // progressTypeList.unshift(all)
      // projectStateList.unshift(all)
      // warningStateList.unshift(all)
      const coordinateTypes = coordinateTypeList.map(tranOptions)
      const progressTypes = progressTypeList.map(tranOptions)
      const projectState = projectStateList.map(tranOptions)
      const warningState = warningStateList.map(tranOptions)

      this.searchData = this.searchData.map(item => {
        item.options = item.name === 'coordinateTypes' ? coordinateTypes : item.options
        item.options = item.name === 'progressTypes' ? progressTypes : item.options
        item.options = item.name === 'projectState' ? projectState : item.options
        item.options = item.name === 'warningState' ? warningState : item.options
        return item
      })
      this.$refs['SearchBar'].setData('states', 'options', data[0]?.billFlow?.form?.formState, 'init')
      // 列表数据初始化
      console.log('列表数据:', data)
      this.table.list = data
      this.table.total = count
    },
    /**
     * @description: 获取列表数据、搜索数据
     * @return {*}
     */
    getTable(currentPage = this.table.currentPage, pageSize = this.table.pageSize) {
      // 获取请求参数
      let postData = this.getSearchData()
      postData = {
        ...postData,
        states: typeof postData.states === 'number' ? [postData.states] : [],
        progressTypes: typeof postData.progressTypes === 'number' ? [postData.progressTypes] : [],
        coordinateTypes: typeof postData.coordinateTypes === 'number' ? [postData.coordinateTypes] : [],
        type: this.filter.type,
        currentPage: currentPage,
        pageSize: pageSize
      }
      // 请求开始
      return GetProjectSiteSignPaging(postData)
    },

    /**
     * @description: 搜索栏数据转换
     * @return {Object} request数据
     */
    getSearchData() {
      if (this.lastSearch === null) {
        this.lastSearch = {}
        this.searchData.forEach(item => {
          const { value, transformType, name } = item
          if (transformType === 'property-serial') {
            value.forEach((ele, index) => {
              const serial = index + 1
              this.lastSearch[name + serial] = ele
            })
          } else {
            this.lastSearch[name] = value
          }
        })
      }
      return this.lastSearch
    },
    /**
     * @description: 路由到详情页
     * @param {*} row
     */
    handleDetails(row) {
      this.$router.push({ name: 'ProjectSiteSignDetails', query: { id: row.id, refreshRouterName: this.$route.name }})
    },
    /**
     * @description: table编辑
     * @param {Object} row
     */
    handleEdit(id = '', type) {
      this.$router.push({ name: 'ProjectSiteSignEdit', query: { type, id, refreshRouterName: this.$route.name }})
    }
  }
}
