import { getProjectInvoicePaging } from '@/api/xmgl/projectInvoice'
import SearchBar from '@/components/SearchBar'
import searchData from './searchData'
import { ExcelExport } from 'pikaz-excel-js'

export default {
  name: 'ProjectInvoice',
  components: {
    SearchBar,
    ExcelExport
  },
  data() {
    return {
      tabPosition: 0,
      size: 'mini', // 整体大小 medium / small / mini
      searchData: searchData, // 渲染搜索栏数据
      amount: 0, // 统计发票金额

      filename: '工程款发票列表',
      exLoading: false,
      sheet: [],

      /* 列表数据相关 */
      table: {
        list: [],
        loading: false,
        total: 0,
        currentPage: 1,
        pageSize: this.utils.getPersonalSettings().pageSize
      }
    }
  },
  activated() {
    console.info('actived')
    this.fetchData()
  },
  created() {
    console.info('created')
    this.fetchData()
  },
  methods: {

    /**
     * @description: 获取列表数据、搜索数据
     * @return {*}
     */
    async fetchData() {
      this.table.loading = true
      try {
        // 获取请求参数
        const postData = this.getSearchData()
        postData.currentPage = this.table.currentPage
        postData.pageSize = this.table.pageSize
        console.log('fetchData:请求数据', postData)

        // 请求开始
        const response = await getProjectInvoicePaging(postData)
        const {
          data,
          count,
          amount
        } = response

        this.amount = amount
        // 搜索栏数据初始化

        // 列表数据初始化
        this.table.list = data
        this.table.total = count
      } catch (e) {
        console.log('fetchData:请求列表数据失败，错误信息：', e)
      } finally {
        this.table.loading = false
      }
    },
    /**
     * @description: 设置表格名字
     */
    setFname() {
      let date = this.utils.parseTime(new Date())
      date = date.replace(' ', '_')
      date = date.replace(':', '_')
      this.filename = this.filename + date
    },
    /**
     * @description:表格导出功能
     */
    async exportExcel() {
      try {
        this.exLoading = true
        const postData = this.getSearchData()
        postData.currentPage = this.table.currentPage
        postData.pageSize = this.table.total
        const { data } = await getProjectInvoicePaging(postData)

        const table = data
        this.sheet = [{
          sheetName: '工程款发票列表',
          keys: [
            'orgName',
            'projectName',
            'number',
            'amount',
            'date',
            'remark',
            'createUserName',
            'createDate'
          ],
          tHeader: [
            '组织机构',
            '项目',
            '票号',
            '金额',
            '开票日期',
            '备注',
            '制单人',
            '制单日期'
          ],
          table
        }]
        this.setFname()
        this.$refs.excelExport.pikaExportExcel()
      } catch (e) {
        console.log(e)
        this.onError('获取表格数据失败！')
      } finally {
        this.exLoading = false
      }
    },
    /**
     * @description: excel导出错误处理
     */
    onError(err) {
      this.$message.info('导出失败：' + err)
    },
    /**
     * @description: 搜索栏数据转换
     * @return {Object} request数据
     */
    getSearchData() {
      const data = {}
      this.searchData.forEach(item => {
        data[item.name] = item.value
      })
      return data
    },
    /**
     * @description: 设置分页大小
     * @param {Number} val
     */
    sizeChange(val) {
      this.table.pageSize = val
      this.fetchData()
    },
    /**
     * @description: 搜索回调
     */
    async handelSearch() {
      await this.fetchData()
    },
    /**
     * @description: 路由到详情页
     * @param {*} row
     */
    handleDetails(row) {
      this.$router.push({ name: 'ProjectInvoiceDetail', query: { id: row.id, refreshRouterName: this.$route.name }, params: { id: row.id }})
    },
    /**
     * @description: table编辑
     * @param {Object} row
     */
    handleEdit(id = '', type) {
      this.$router.push({ name: 'ProjectInvoiceEdit', query: { type, id, refreshRouterName: this.$route.name }, params: { id: id, type: 3 }})
    }
  }
}
