import { getBussinessInfoEditInit, getBussinessInfoById } from '@/api/ywgl/bussinessInfo'
import FlowDisplay from '@/components/Flow/flowDisplay'
import FlowAttitude from '@/components/Flow/flowAttitude'
// import UpFile from '@/components/UpLoad/UpFile'
export default {
  name: 'BussinessInfoCheckup',
  components: {
    FlowAttitude,
    FlowDisplay
    // UpFile
  },
  data() {
    return {
      ruleForm: {
        orgId: null,
        companyId: this.$store.state.user.selectOrgId,
        professionalType: null,
        informationSource: null,
        area: null,
        progress: null,
        marketCategory: null,
        cooperationMode: null,
        projectName: '',
        generalContractor: '',
        builder: '',
        projectSurvey: '',
        developer: null,
        linkMan: '',
        intermediary: '',
        intermediaryTel: '',
        address: '',
        houseProperty: null,
        floors: '',
        buildingStructure: '',
        decorationArea: null,
        totalArea: null,
        cost: null,
        paymentTerms: '',
        tenderMode: null,
        tenderEndDate: '',
        startDate: '',
        finishDate: '',
        currentStatus: '',
        currentStatusDate: '',
        dockPerson: '',
        trackDepth: null,
        trackPerson: null,
        marketingMember: null,
        tenderSections: null,
        competitors: '',
        remark: '',
        isComplete: true,
        upFile: [],
        fileList: []
      },
      treeData: null,
      cooperationModeList: [], // 合作模式
      marketCategoryList: [],
      professionalTypeList: [],
      progressList: [],
      tenderModeList: [],
      trackDepthList: [],
      informationSourceList: [],
      housePropertyList: [],
      developerList: [],
      buttonLoading: false,
      refreshRouterName: ''
    }
  },
  mounted() {
  },
  activated() {
    this.initData()
    if (this.$route.query.id) {
      this.refreshRouterName = this.$route.params.refreshRouterName
    }
  },
  methods: {
    initData() {
      new Promise((resolve, reject) => {
        getBussinessInfoEditInit().then(response => {
          const { data, result1, result2, result3, result4, result5, result6, result7, result8, result9 } = response
          this.cooperationModeList = result1
          this.marketCategoryList = result2
          this.professionalTypeList = result3
          this.progressList = result4
          this.tenderModeList = result5
          this.trackDepthList = result6
          this.informationSourceList = result7
          this.housePropertyList = result8
          this.developerList = result9

          data.forEach(element => {
            element.label = element.name
          })
          resolve(data)
        })
      }).then(treeData => {
        this.bussinessInfoId = this.$route.query.id
        if (this.bussinessInfoId) {
          getBussinessInfoById({ id: this.bussinessInfoId }).then(response => {
            const { data, files } = response
            console.log(data)
            this.treeData = treeData
            this.ruleForm = data

            this.fileList = files // 附件
          })
        } else {
          this.treeData = treeData
        }
      }).catch(() => {
        this.$message.error('信息错误')
      })
    },
    closeThePage() {
      this.utils.closeAndRefresh(this.$route.query.refreshRouterName)
    }
  }
}
