import { getCityList } from '@/api/xtgl/city'
import AddCity from './components/addCity'
import { getTree } from '@/utils/index'
export default {
  name: 'City',
  components: {
    AddCity
  },
  data() {
    return {
      list: [],
      loading: true,
      dialogTableVisible: false,
      dialogTitle: '保存',
      currentRow: null,
      pid: 0,
      defaultProps: {
        children: 'children',
        label: 'name'
      }
    }
  },
  mounted() {
    this.fetchData()
  },
  methods: {
    fetchData() {
      this.getPorperty()
    },
    getPorperty() {
      getCityList().then(response => {
        const { data } = response
        this.list = getTree(0, data)
      })
    },
    dialogClose() {
      this.dialogTableVisible = false
      this.getPorperty()
    },
    update(data) {
      this.currentRow = data
      this.dialogTableVisible = true
    },
    append(node, data) {
      this.currentRow = null
      this.pid = data.id
      this.dialogTableVisible = true
    },
    add() {
      this.currentRow = null
      this.pid = 0
      this.dialogTableVisible = true
    }
  }
}
