import { GetProjectCompleteManagePaging } from '@/api/xmgl/ProjectCompleteManage.js'
import SearchBar from '@/components/SearchBar'
import TableEx from '@/components/TableEx'
// import searchData from './searchData'
export default {
  name: 'ProjectCompleteManageIndex',
  components: {
    SearchBar,
    TableEx
  },
  data() {
    return {
      size: 'mini', // 整体大小 medium / small / mini
      searchData: require('./searchData').default(), // 渲染搜索栏数据
      lastSearch: null, // 最近一次搜索的数据
      filter: { // 过滤数据
        type: '3', // 类型
        typeOptions: [ // 类型数组
          {
            value: '0',
            name: '我发起的'
          },
          {
            value: '1',
            name: '待审批'
          },
          {
            value: '2',
            name: '已审批'
          },
          {
            value: '3',
            name: '列表'
          }
        ]
      },
      /* 列表数据相关 */
      table: {
        columns: [
          {
            label: '编号', // 表头
            prop: 'number', // 渲染的属性
            class: 'link', // class
            minWidth: '180', // 最小宽度
            click: (row) => this.handleDetails(row)
          },
          {
            label: '项目',
            prop: 'projectName',
            minWidth: '200'
          },
          {
            label: '组织机构',
            prop: 'orgName',
            minWidth: '120'
          },
          {
            label: '实际开工日期',
            format: 'date',
            prop: 'realStartDate',
            width: '120'
          },
          {
            label: '竣工验收时间',
            format: 'date',
            prop: 'completDate',
            width: '120'
          },
          {
            label: '实际工期',
            prop: 'realDuration',
            width: '92'
          },
          {
            label: '顺延后工期',
            prop: 'syhgq',
            width: '92'
          },
          {
            label: '剩余天数',
            prop: 'sysj',
            width: '92'
          },
          {
            business: 'state' // 业务部分的快捷使用参考 src\components\TableEx\Column\businessList.js
          },
          {
            label: '待处理',
            prop: 'pendingPerson',
            format: (val) => Array.isArray(val) ? val.join('、') : val,
            width: '100'
          },
          {
            business: 'createUser' // 业务部分的快捷使用参考 src\components\TableEx\Column\businessList.js
          }
        ],
        list: [],
        exLoading: false, // 导出loading
        tbLoading: false, // table loading
        total: 0,
        currentPage: 1,
        pageSize: this.utils.getPersonalSettings().pageSize
      }
    }
  },
  beforeRouteEnter(to, from, next) {
    next(vm => {
      vm.fetchData()
    })
  },
  methods: {
    fetchData(type) { // SearchBar 事件类型 重置:reset  搜索:search
      switch (type) {
        case 'filter':
          this.table.currentPage = 1
          break
        case 'reset':
        case 'search':
          this.table.currentPage = 1
          this.lastSearch = null
          break
      }
      this.table.tbLoading = true
      this.getTable()
        .then(res => this.parseServeDate(res))
        .finally(() => {
          this.table.tbLoading = false
        })
    },
    /**
     * @description:表格导出功能
     */
    /**
     * @description:表格导出功能
     */
    async exportExcel() {
      const Excel = this.$refs['TableEx'].initExcel()
      this.getTable(1, this.table.total).then(({ data }) => {
        Excel.format([{ ...Excel.sheet[0], table: data }]).export()
      }).catch(() => {
        this.$message.error('导出失败')
        Excel.cancel()
      })
    },
    parseServeDate(res) {
      const {
        data,
        count,
        orderTypeList
      } = res

      // 搜索栏数据初始化
      this.$refs['SearchBar'].setData('states', 'options', data[0]?.billFlow?.form?.formState, 'init')
      this.$refs['SearchBar'].setData('orderTypes', 'options', orderTypeList, 'init')
      // 列表数据初始化
      console.log(data)
      this.table.list = data
      this.table.total = count
    },
    /**
     * @description: 获取列表数据、搜索数据
     * @return {*}
     */
    getTable(currentPage = this.table.currentPage, pageSize = this.table.pageSize) {
      // 获取请求参数
      let postData = this.getSearchData()
      postData = {
        ...postData,
        orderTypes: postData.orderTypes ? [postData.orderTypes] : [],
        states: postData.states ? [postData.states] : [],
        currentPage,
        pageSize,
        type: this.filter.type
      }
      // 请求开始
      return GetProjectCompleteManagePaging(postData)
    },
    /**
     * @description: 搜索栏数据转换
     * @return {Object} request数据
     */
    getSearchData() {
      if (this.lastSearch === null) {
        this.lastSearch = {}
        this.searchData.forEach(item => {
          const { value, transformType, name } = item
          if (transformType === 'property-serial') {
            value.forEach((ele, index) => {
              const serial = index + 1
              this.lastSearch[name + serial] = ele
            })
          } else {
            this.lastSearch[name] = value
          }
        })
      }

      return this.lastSearch
    },
    /**
     * @description: 路由到详情页
     * @param {*} row
     */
    handleDetails(row) {
      this.$router.push({ name: 'ProjectCompleteManageDetails', query: { id: row.id, refreshRouterName: this.$route.name }})
    },
    /**
     * @description: table编辑
     * @param {Object} row
     */
    handleEdit(id = '', type) {
      this.$router.push({ name: 'ProjectCompleteManageEdit', query: { type, id, refreshRouterName: this.$route.name }})
    }
  }
}
