import { GetExpendById } from '@/api/cwgl/zjgl/zjzc.js'

export default {
  // 组件名称
  name: '',
  // 局部注册的组件
  components: {},
  // 组件参数 接收来自父组件的数据
  props: {},
  // 组件状态值
  data() {
    return {
      dialogVisible: false, // 控制对话框显示
      id: 1,
      ruleForm: {
        number: '', // 收款编号
        department: '', // 部门
        sktyp: '', // 收款类型
        zjtyp: '', // 资金类型
        date1: '', // 收款日期
        handlers: '', // 经手人
        amount: '', // 金额
        bank: '', // 银行
        note: '' // 备注
      },
      tableData: [
        // 承兑明细数据
        {
          date: '2016-05-02',
          name: '王小虎',
          address: '上海市普陀区金沙江路 1518 弄'
        },
        {
          date: '2016-05-04',
          name: '王小虎',
          address: '上海市普陀区金沙江路 1517 弄'
        },
        {
          date: '2016-05-01',
          name: '王小虎',
          address: '上海市普陀区金沙江路 1519 弄'
        }
      ],
      // 验证规则
      rules: {
        number: [
          { required: true, message: '请输入收款编号', trigger: 'blur' }
        ],
        handlers: [
          { required: true, message: '请输入经手人', trigger: 'blur' }
        ],
        amount: [{ required: true, message: '请输入金额', trigger: 'blur' }],
        department: [
          { required: true, message: '请选择公司', trigger: 'change' }
        ],
        zjtyp: [
          { required: true, message: '请选择收款类型', trigger: 'change' }
        ],
        sktyp: [
          { required: true, message: '请选择收款类型', trigger: 'change' }
        ],

        date1: [
          {
            type: 'date',
            required: true,
            message: '请选择日期',
            trigger: 'change'
          }
        ],
        resource: [
          { required: true, message: '请选择活动资源', trigger: 'change' }
        ],
        desc: [{ required: true, message: '请填写活动形式', trigger: 'blur' }]
      }
    }
  },
  // 计算属性
  computed: {},
  // 侦听器
  watch: {},
  // 生命周期钩子   注：没用到的钩子请自行删除
  /**
   * 组件实例创建完成，属性已绑定，但DOM还未生成，$ el属性还不存在
   */
  created() {
    this.getZcForm()
  },
  /**
   * el 被新创建的 vm.el 替换，并挂载到实例上去之后调用该钩子。
   * 如果 root 实例挂载了一个文档内元素，当 mounted 被调用时 vm.el 也在文档内。
   */
  mounted() {},
  // 组件方法
  methods: {
    getZcForm() {
      GetExpendById(this.id).then(
        res => {
          const { code,
            data,
            gatheringTypeOptions,
            capitalTypeOptions } = res
          if (code === 200) {
            console.log(res, 777)
          }
        }

      )
    },
    dialogVisibleShow() {
      console.log('测试')
      this.dialogVisible = true
    },
    indexMethod(index) {
      index = String(index + 1)
      const length = index.length
      if (index.length < 2) {
        for (let i = 0; i < 2 - length; i++) {
          index = '0' + index
        }
        return index
      } else {
        return index
      }
    },
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          alert('submit!')
        } else {
          console.log('error submit!!')
          return false
        }
      })
    },
    resetForm(formName) {
      this.$refs[formName].resetFields()
    },
    addRow() {
      this.tableData.push({
        date: '',
        name: '',
        address: ''
      })
    },
    deleteRow(index, rows) {
      rows.splice(index, 1)
    },
    handleClose(done) {
      this.$confirm('确认关闭？')
        .then(_ => {
          done()
        })
        .catch(_ => {})
    }
  }
}
