import { getPostPoolList, getPostPoolById, getPostPoolDetailById } from '@/api/rsgl/postPool'
import { getPersonPostEditInit } from '@/api/rsgl/person'
import { addUserPost, putUserPost } from '@/api/rsgl/userPost'
import OrgSelect2 from '@/components/OrgSelect/index2'
export default {
  name: 'UserPostDetailEdit',
  components: {
    OrgSelect2
  },
  props: {
    obj: {
      type: Object,
      default: null
    },
    userVal: {
      type: Number,
      default: 0
    }
  },
  data() {
    return {
      ruleForm: {
        userId: null,
        postId: null,
        orgId: null,
        postPoolDetailId: null,
        roleId: null,
        isMain: false,
        inUse: true
      },
      keyPostPool: 0,
      orgList: [], // 所有的组织机构数据
      postPoolList: [], // 岗位池数据
      postList: [],
      roleList: [],
      loadOrg: false,
      buttonLoading: false,
      rules: {
        postPoolDetailId: [{ required: true, message: '必填', trigger: 'blur' }],
        postId: [{ required: true, message: '必填', trigger: 'blur' }],
        roleId: [{ required: true, message: '必填', trigger: 'blur' }],
        orgId: [{ required: true, message: '必填', trigger: 'blur' }]
      }
    }
  },
  mounted() {
    this.initData()
    const promise = this.getLeader()
    promise.then(() => {
      if (this.obj) {
        var newObj = JSON.parse(JSON.stringify(this.obj))
        this.ruleForm = newObj
        // 拼接岗位池id成二级数组，来匹配 选岗位池 级联菜单
        var postPoolIdArray = []
        postPoolIdArray.push(newObj.postPoolDetail.postPoolId)
        postPoolIdArray.push(newObj.postPoolDetailId)
        this.ruleForm.postPoolDetailId = postPoolIdArray
        this.keyPostPool += 1
      } else {
        this.loadOrg = true
      }
      console.log(this.userVal)
    })
  },
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          var newObj = JSON.parse(JSON.stringify(this.ruleForm))
          // 级联中最后一个id
          var computerpostPoolDetailId = this.ruleForm.postPoolDetailId[this.ruleForm.postPoolDetailId.length - 1]
          newObj.postPoolDetailId = computerpostPoolDetailId
          newObj.userId = this.userVal
          if (this.obj) {
            this.buttonLoading = true
            putUserPost(newObj)
              .then((response) => {
                this.$message(response.message)
                this.buttonLoading = false
                this.$emit('dialogClose')
              }).catch(() => { this.buttonLoading = false })
          } else {
            addUserPost(newObj)
              .then((response) => {
                this.$message(response.message)
                this.buttonLoading = false
                this.$emit('dialogClose')
              }).catch(() => { this.buttonLoading = false })
          }
        }
      })
    },
    initData() {
      getPersonPostEditInit({ companyId: 1 }).then(response => {
        const { data1, data2, data3 } = response
        this.orgList = data2
        this.postList = data1
        this.roleList = data3
        this.loadOrg = true
      })
    },
    getLeader() {
      const pro1 = new Promise((resolve, reject) => {
        getPostPoolList().then(response => {
          const { data } = response
          this.postPoolList = data.map((value, i) => {
            var child = []
            getPostPoolById({ id: value.id }).then(response => {
              const { data } = response
              data.postPoolDetail.forEach(e => {
                child.push({
                  value: e.id,
                  label: e.post.name
                })
              })
            })

            return {
              value: value.id,
              label: value.name,
              children: child
            }
          })
          this.keyPostPool += 1
          resolve()
        }).catch(() => { reject() })
      })
      return pro1
    },
    getPostRole(selVal) {
      var computerpostPoolDetailId = this.ruleForm.postPoolDetailId[this.ruleForm.postPoolDetailId.length - 1]
      getPostPoolDetailById({ id: computerpostPoolDetailId }).then(response => {
        const { data } = response
        this.ruleForm.postId = data.postId
        this.ruleForm.roleId = data.roleId
      })
    }
  }
}
