import API from '@/api/cwgl/zjgl/zjsk.js'
import SearchBar from '@/components/SearchBar'
import searchData from './chengDuiDaoQiSearch'
export default {
  name: 'ZiJinShouKuan',
  components: {
    SearchBar
  },
  data() {
    return {
      searchData: searchData, // 渲染搜索栏数据

      /* 列表数据相关 */
      table: {
        list: [],
        loading: false,
        total: 0,
        currentPage: 1,
        pageSize: 10
      }
    }
  },
  created() {
    this.fetchData()
  },
  methods: {
    /**
     * @description: 获取列表数据、搜索数据
     * @return {*}
     */
    async fetchData() {
      this.table.loading = true
      try {
        // 获取请求参数
        const postData = this.getSearchData()
        postData.currentPage = this.table.currentPage
        postData.pageSize = this.table.pageSize
        console.log('fetchData:请求数据', postData)
        // 请求开始
        const response = await API.GetPaymentPaging(postData)
        const { data, count, capitalTypeOptions, paymentTypeOptions } = response

        // 搜索栏数据初始化
        capitalTypeOptions.forEach(ele => { ele.label = ele.name }) // 资金形式选项数据转换
        paymentTypeOptions.forEach(ele => { ele.label = ele.name }) // 收款类型选项数据转换
        this.searchData = this.searchData.map((item) => {
          item.options = item.name === 'paymentTypes' ? paymentTypeOptions : []
          item.options = item.name === 'capitalTypes' ? capitalTypeOptions : []
          return item
        })

        // 列表数据初始化
        this.table.list = data
        this.table.total = count
      } catch (e) {
        console.log('fetchData:请求列表数据失败，错误信息：', e)
      } finally {
        this.table.loading = false
      }
    },
    /**
     * @description: 搜索栏数据转换
     * @return {Object} request数据
     */
    getSearchData() {
      const data = {}
      this.searchData.forEach((item) => {
        if (item.name === 'dateRange') {
          data.startDate = item.value[0]
          data.endDate = item.value[1]
        } else if (item.name === 'moneySection') {
          data.startAmount = item.value[0]
          data.endAmount = item.value[1]
        } else {
          data[item.name] = item.value
        }
      })
      return data
    },
    /**
     * @description: 修改每页显示条目个数
     * @param {*} val
     */
    sizeChange(val) {
      this.table.pageSize = val
      this.fetchData()
    },
    /**
     * @description: 搜索
     */
    HandelSearch() {
      this.fetchData()
    },
    /**
     * @description: table 行的 className 的回调方法，也可以使用字符串为所有行设置一个固定的 className。
     * @param {*} row 当前行数据
     * @param {*} rowIndex 当前行index
     * @return {*}
     */
    tableRowClassName({ row, rowIndex }) {
      if (rowIndex % 2 === 0) {
        return 'row-bg-color'
      }
      return ''
    }
  }
}
