import { getWorkerOrganizationList, getDefaultOptions } from '@/api/zggl/workerOrg'
import SearchBar from '@/components/SearchBar'
import searchData from './searchData'
export default {
  name: 'WorkerOrg',
  components: {
    SearchBar
  },
  data() {
    return {
      size: 'mini',
      searchData: searchData, // 渲染搜索栏数据
      /* 列表数据相关 */
      table: {
        list: [],
        loading: false
      }
    }
  },
  beforeRouteEnter(to, from, next) {
    next(vm => {
      vm.fetchData()
    })
  },
  methods: {
    /**
     * @description: 获取列表数据、搜索数据
     * @return {*}
     */
    async fetchData() {
      this.table.loading = true
      try {
        // 获取请求参数
        const postData = this.getSearchData()
        // 请求开始
        const response = await getWorkerOrganizationList({
          ...postData
        })
        const { data } = response
        // 初始化数据获取
        const default_options = await getDefaultOptions()
        const { personTypeOptions, postTypeOptions } = default_options
        personTypeOptions.forEach(ele => {
          // 人员类别选项数据转换
          ele.label = ele.name
        })
        postTypeOptions.forEach(ele => {
          // 职务选项数据转换
          ele.label = ele.name
        })

        // 搜索栏数据初始化
        this.searchData = this.searchData.map(item => {
          item.options =
            item.name === 'posts' ? postTypeOptions : item.options
          return item
        })

        // 列表数据初始化
        this.table.list = data
      } catch (e) {
        console.log('fetchData:请求列表数据失败，错误信息：', e)
      } finally {
        this.table.loading = false
      }
    },
    /**
     * @description: 搜索栏数据转换
     * @return {Object} request数据
     */
    getSearchData() {
      const data = {}
      this.searchData.forEach(item => {
        // if (item.name === 'year') {
        //   data['year'] = new Date(item.value).getFullYear()
        // } else {
        //   data[item.name] = item.value
        // }
        data[item.name] = item.value
      })
      return data
    },
    /**
     * @description: 搜索回调
     */
    handelSearch() {
      this.fetchData()
    },
    /**
     * @description: 路由到详情页
     * @param {*} row
     */
    handleDetails(row) {
      this.$router.push({ name: 'WorkerOrgDetails', query: { id: row.id, refreshRouterName: this.$route.name }})
    },
    /**
     * @description: table编辑
     * @param {Object} id
     */
    handleEdit(id) {
      this.$router.push({ name: 'WorkerOrgEdit', query: { id, refreshRouterName: this.$route.name }})
    }
  }
}
