import { getWorkerList } from '@/api/ggzx/worker'
import SearchBar from '@/components/SearchBar'
import searchData from './searchData'
export default {
  name: 'WorkerSelect',
  components: {
    SearchBar
  },
  props: {
    dialogWidth: { type: String, default: '70%' }, // 弹出层的宽度
    multiple: { type: Boolean, default: true }, // 默认多选
    type: { type: Number, default: null },
    workerList: { type: Array, default: () => [] }, // 默认选中工人组
    orgIds: { type: Array, default: () => [] }, // 默认选中项目组
    year: { type: Number, default: new Date().getFullYear() }, // 年份-默认当年
    bdState: { type: Boolean, default: null }, // 是否报到
    ispause: { type: Boolean, default: null },
    isexit: { type: Boolean, default: null }
  },
  data() {
    return {
      open: false,
      disabled: false,
      buttonLoading: false,
      selectionItem: [],
      tableList: [],
      tableLoading: null,
      searchData: searchData,
      refresh: false
    }
  },
  activated() {
    this.fetchData()
  },
  mounted() {
    this.fetchData()
  },
  methods: {
    openDialog() {
      // 清空数据
      this.selectionItem = []
      this.tableList = []
      this.searchData = this.searchData.map(item => {
        item.value =
            item.name === 'year' ? this.year : item.value
        item.value =
            item.name === 'orgIds' ? this.orgIds : item.value
        item.value =
            item.name === 'number' ? '' : item.value
        item.value =
            item.name === 'name' ? '' : item.value
        return item
      })
      this.open = true
      this.refresh = true
      this.goSearch()
    },
    fetchData() {
      this.goSearch()
    },
    goSearch() {
      const postData = this.getSearchData()
      this.buttonLoading = true
      setTimeout(() => {
        this.getPersonList(postData)
      }, 700)
    },
    /**
     * @description: 搜索栏数据转换
     * @return {Object} request数据
     */
    getSearchData() {
      const lastSearch = {}
      this.searchData.forEach(item => {
        const { value, transformType, name } = item
        if (transformType === 'property-serial') {
          value.forEach((ele, index) => {
            const serial = index + 1
            lastSearch[name + serial] = ele
          })
        } else {
          lastSearch[name] = value
        }
      })
      return lastSearch
    },
    getPersonList(postData) {
      getWorkerList({
        ...postData,
        year: this.year,
        bdState: this.bdState,
        ispause: this.ispause,
        isexit: this.isexit,
        isRefresh: this.refresh
      }).then(res => {
        const { data, yearOptions } = res
        this.tableList = data
        data.forEach(e => {
          var row = this.workerList.find(f => f.id === e.id)
          if (row) {
            this.$refs.multipleTable.toggleRowSelection(e, true)
          } else {
            this.$refs.multipleTable.toggleRowSelection(e, false)
          }
        })
        this.buttonLoading = false
        this.searchData = this.searchData.map(item => {
          item.options =
              item.name === 'year' ? yearOptions : item.options
          return item
        })
      }).catch(() => { this.buttonLoading = false })
    },
    save() {
      this.buttonLoading = true
      // 触发父组件的事件
      setTimeout(() => {
        this.$emit('getPersons', this.selectionItem)
        this.buttonLoading = false
        this.open = false
      }, 300)
    },
    // 多选框选中数据
    handleSelectionChange(selection) {
      this.selectionItem = selection
    }
  }
}
