import { addPriceList, getBillFlow } from '@/api/cggl/priceList'
import OrgSelect2 from '@/components/OrgSelect/index2'
import { getPersonList } from '@/api/rsgl/person'
import { getSupplierInfo } from '@/api/cggl/supplier'
import FlowAttitude from '@/components/Flow/flowAttitude'
export default {
  name: 'PriceListAdd',
  components: {
    OrgSelect2,
    FlowAttitude
  },
  props: {
    obj: {
      type: Object,
      default: null
    }
  },
  data() {
    return {
      ruleForm: {
        orgId: null
      },
      loading: false,
      showbuttons: false,
      activate: false,
      dialogTableVisible: false,
      dialogTitle: '保存',
      dialogWidth: '70%',
      currentRow: null,
      listIndex: 0,
      list: [],
      fileList: [],
      planTypeList: null,
      columnShow: { unit2: true,
        brand: true,
        model: true,
        spec: true,
        thickness: true,
        texture: true,
        surfaceTreatment: true,
        designNumber: true
      },
      personList: [],
      multipleSelection: [],
      // 全选供应商
      checkedSite: false,
      currentValsite: '',
      // 供应商
      suppliers: [],
      suppliersCopy: [],
      // 流程
      loadFlow: false,
      attitudePara: null,
      active: 0,
      formData: {
        supplier_id: [],
        supplier_check: []
      },
      rules: {
        orgId: [{ required: true, message: '请选择', trigger: 'change' }],
        supplierId: [{ required: true, message: '请选择', trigger: 'blur' }]
      }
    }
  },
  computed: {
    getOrgList() {
      return this.$store.state.user.info.orgList
    }
  },
  watch: {
    // 'ruleForm.orgId': {
    //   handler: function(value, oldvalue) {
    //     if (!oldvalue) {
    //       this.$refs['ruleForm'].validateField('orgId', (valid) => {})
    //     }
    //   }, deep: true
    // }
  },
  activated() {
  },
  mounted() {
    this.initData()
  },
  methods: {
    saveForm() {
    },
    submitForm(formName, state) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          var postData = {}
          var newObj = JSON.parse(JSON.stringify(this.ruleForm))
          // 拼接材料明细参数
          var arr1 = []
          this.multipleSelection.forEach(e => {
            var item = {}
            item.planDetailId = e.id
            item.quantity = e.quantity
            item.price = e.price
            // item.materialId = e.materialId
            arr1.push(item)
          })
          postData.orgId = newObj.orgId
          postData.planDate = newObj.planDate
          postData.supplierId = newObj.supplierId
          postData.suppliers = this.formData.supplier_id.join(',')
          postData.useSpace = newObj.useSpace
          postData.executeUserId = newObj.buyerId
          postData.priceExecuteDate = newObj.priceExecuteDate
          postData.planId = newObj.id
          postData.state = state
          postData.pricelistDetails = arr1

          this.loading = true
          addPriceList(postData).then(response => {
            this.$message(response.message)
            this.loading = false
            this.setData(response)
          }).catch(() => { this.loading = false })
        }
      })
    },
    async initData() {
      // 用户
      getPersonList().then(response => {
        this.personList = response.data
      })
      // 供应商  name id
      getSupplierInfo().then((response) => {
        this.suppliers = response.data
        this.suppliersCopy = response.data
      })
      if (this.obj) {
        this.list = this.obj.data
        this.ruleForm = {
          orgStr: this.obj.orgStr,
          taskNumber: this.obj.taskNumber
        }
      }
      this.setColumnShow()
    },
    setData(response) {
      const { data } = response
      var pom = []
      data.forEach(e => {
        pom.push({
          id: e.id,
          categoryId: e.material.categoryId,
          category: { id: e.material.categoryId, name: e.material.materialCategory.name },
          unit: e.material.unit,
          unit2: e.material.unit2,
          brand: e.material.brand,
          model: e.material.model,
          spec: e.material.spec,
          thickness: e.material.thickness,
          texture: e.material.texture,
          surfaceTreatment: e.material.surfaceTreatment,
          designNumber: e.material.designNumber,
          applicationQuantity: e.applicationQuantity,
          applicationQuantity2: e.applicationQuantity2,
          quantity: e.quantity,
          quantity2: e.quantity2,
          entryDate: e.entryDate,
          remark: e.remark,
          materialId: e.materialId,
          fileList: response.upFile.filter(f => f.tableId === e.id)
        })
      })
      // 填充参数
      // this.ruleForm = data
      // 填充 明细列表
      this.list = pom
      this.setColumnShow()
      this.loading = false // loading 状态关闭
      this.showbuttons = true // 初始化完成，显示上面的按钮
      this.activate = true // 加载部门组件
    },
    setColumnShow() { // 设置列的显示。列中，都没有值，就隐藏
      var modelunit2 = false
      var modelbrand = false
      var modelmodel = false
      var modelspec = false
      var modelthickness = false
      var modeltexture = false
      var modelsurfaceTreatment = false
      var modeldesignNumber = false
      for (var row of this.list) {
        if (row.unit2) { modelunit2 = true }
        if (row.brand) { modelbrand = true }
        if (row.model) { modelmodel = true }
        if (row.spec) { modelspec = true }
        if (row.thickness) { modelthickness = true }
        if (row.texture) { modeltexture = true }
        if (row.surfaceTreatment) { modelsurfaceTreatment = true }
        if (row.designNumber) { modeldesignNumber = true }
      }
      this.columnShow['unit2'] = modelunit2
      this.columnShow['brand'] = modelbrand
      this.columnShow['model'] = modelmodel
      this.columnShow['spec'] = modelspec
      this.columnShow['thickness'] = modelthickness
      this.columnShow['texture'] = modeltexture
      this.columnShow['surfaceTreatment'] = modelsurfaceTreatment
      this.columnShow['designNumber'] = modeldesignNumber
    },
    closeThePage() {
      this.utils.closeAndRefresh(this.$route.query.refreshRouterName)
    },
    dialogClose(data, state) {
      this.addMaterial(data, state)
      this.setColumnShow()
      this.dialogTableVisible = false
    },
    // showFileList(fileList) {
    //   this.dialogTitle = '附件列表'
    //   this.comName = 'UpFile'
    //   this.dialogWidth = '50%'
    //   this.fileList = fileList
    //   this.dialogTableVisible = true
    // },
    // getRemotePerson(query) {
    //   query = query.trim()
    //   if (query !== '') {
    //     this.loading = true
    //     setTimeout(() => {
    //       getPersonList().then(response => {
    //         this.personList = response.data
    //         this.loading = false
    //       })
    //     }, 200)
    //   } else {
    //     this.personList = []
    //   }
    // },
    getRemotePerson(query) {
      if (query.length > 0) {
        query = query.trim()
        setTimeout(() => {
          getPersonList({ name: query }).then(response => {
            this.personList = response.data
          })
        }, 200)
      } else {
        getPersonList().then(response => {
          this.personList = response.data
        })
      }
    },
    // 材料列表多选选中数据
    handleSelectionChange(selection) {
      this.multipleSelection = selection
    },
    // 选择供应商
    changeSelectsite(val) {
      if (val.length === 0) {
        this.suppliers = this.suppliersCopy
      }
      if (val.length === this.suppliers.length) {
        this.checkedSite = true
      } else {
        this.checkedSite = false
      }

      // 建议中标单位数据源
      var arr = []
      val.forEach(e => {
        arr.push(this.suppliersCopy.filter(f => f.id === e))
      })
      var arr_da = []
      arr.forEach(e => {
        arr_da.push(e[0])
      })
      this.formData.supplier_check = arr_da
    },
    // 模糊搜索-供应商
    filterChecksite(currentValsite) {
      if (currentValsite.length > 0) {
        const arr = []
        this.formData.supplier_id.map((row) => {
          arr.push(...this.suppliersCopy.filter((item) => item.id === row))
        })
        this.suppliers = this.suppliersCopy.filter((item) => {
          if (item.name.includes(currentValsite)) {
            return true
          }
        })
        this.suppliers.push(...arr)
        this.suppliers = Array.from(new Set(this.suppliers))
        this.currentValsite = currentValsite
      } else {
        this.suppliers = this.suppliersCopy
        this.currentValsite = ''
      }
    },
    // 全选供应商
    selectAllsite() {
      this.formData.supplier_id = []
      if (this.checkedSite === true) {
        this.suppliers.map((item) => {
          this.formData.supplier_id.push(item.id)
        })
      } else {
        this.formData.supplier_id = []
      }
    },
    getDynamicFlow() {
      // 下一步获取流程
      var newObj = JSON.parse(JSON.stringify(this.ruleForm))
      // 拼接材料明细参数
      var arr1 = []
      this.list.forEach(e => {
        var m = {}
        m.categoryId = e.categoryId
        m.categoryName = e.category.name
        // m.unit = e.unit
        m.unit2 = e.unit2
        m.brand = e.brand
        m.model = e.model
        m.spec = e.spec
        m.thickness = e.thickness
        m.texture = e.texture
        m.surfaceTreatment = e.surfaceTreatment
        m.designNumber = e.designNumber
        var item = {}
        item.material = m
        // item.fileList = []
        // if (e.fileList.length) {
        //   item.fileList = e.fileList.map(m => { return { id: m.id, addFlag: m.addFlag } })
        // }
        item.id = e.id
        item.isMain = e.category.isMain
        item.applicationQuantity = e.applicationQuantity
        item.applicationQuantity2 = e.applicationQuantity2 || 0
        item.quantity = e.quantity
        item.quantity2 = e.quantity2
        item.entryDate = e.entryDate
        item.remark = e.remark
        item.targetCostMaterialId = e.targetCostMaterialId
        arr1.push(item)
      })
      if (arr1.length === 0) {
        this.$message('明细不能为空！')
        return
      }

      newObj.planDetail = arr1
      this.loading = true

      getBillFlow(newObj).then(response => {
        this.loading = false
        if (response.success) {
          this.billFlow = response.data.billFlow
          this.active = 1
        } else {
          this.$message(response.message)
        }
      }).catch(() => { this.loading = false })
    }
  }
}
