import OrgSelect2 from '@/components/OrgSelect/index2'
import { getContractPaging } from '@/api/cggl/contract'
export default {
  name: 'ContractCheck',
  components: {
    OrgSelect2
  },
  props: {
    obj: {
      type: Object,
      default: null
    }
  },
  data() {
    return {
      ruleForm: {
      },
      contractList: [],
      search: {
        number: '',
        orgIds: [],
        states: [],
        supplierIds: [],
        categoryName: ''
      },
      total: 0,
      currentPage: 1,
      pageSize: this.utils.getPersonalSettings().pageSize,
      loading: false,
      listLoading: false
    }
  },
  computed: {
  },
  watch: {
  },
  activated() {
  },
  mounted() {
    this.initData()
  },
  methods: {
    async initData() {
      this.listLoading = true
      var postData = {}
      postData.currentPage = this.currentPage
      postData.pageSize = this.pageSize
      postData.number = this.search.number
      postData.orgIds = this.search.orgIds
      postData.states = this.search.states
      postData.states.push(10000) // 已确认的合同
      postData.supplierIds = this.search.supplierIds
      postData.categoryName = this.search.categoryName
      getContractPaging(postData).then(response => {
        const { data, count } = response
        this.contractList = data
        this.total = count
        this.listLoading = false
      })
    },
    checkContract() {
      var data = this.ruleForm
      if (!data.number) {
        this.$message.error('请选择合同。')
      } else {
        this.$emit('dialogClose', data, 'checkContract')
      }
    },
    handleCurrentChange(val) {
      this.loading = true
      this.ruleForm = val
      this.loading = false
    },
    sizeChange(val) {
      this.pageSize = val
      this.initData()
    }
  }
}
