import { getPersonListWithType } from '@/api/rsgl/person'
import SelectPerson2 from '@/components/Other/selectPerson2'
export default {
  name: 'PersonSelect3',
  components: { SelectPerson2 },
  model: { event: 'change', prop: 'value' },
  props: {
    disabled: {
      type: Boolean,
      default: false
    },
    multiple: {
      type: Boolean,
      default: false
    },
    placeholder: {
      type: String,
      default: '选择'
    },
    size: {
      type: String,
      default: 'small'
    },
    value: {
      type: [Array, String, Number],
      default: ''
    },
    type: {
      type: Number,
      default: null
    }
  },
  data() {
    return {
      personList: [],
      loading: false
    }
  },
  computed: {
    inputVal: {
      set(val) {
        // console.log(val)
        this.$emit('change', val)
      },
      get() {
        return this.value
      }
    }
  },
  created() {
    this.getPersonList()
  },
  methods: {
    getPersons(personList) {
      if ((personList || []).length > 0) {
        this.$nextTick(() => {
          if (this.multiple) {
            this.inputVal = [...this.inputVal, ...personList.map(item => item.id)]
          } else {
            this.inputVal = personList[0].id
          }
        })
      }
    },
    selectPerson() {
      this.$refs.elSelect.blur()
      this.$refs.personRef.openDialog()
    },
    async getPersonList() {
      try {
        this.loading = true
        const postData = {}
        postData.personType = this.type
        const res = await getPersonListWithType()
        this.personList = res.data
      } finally {
        this.loading = false
      }
    }
  }
}
