import { GetAllList, getPermit, deletePermit } from '@/api/xtgl/page'
import { getTree } from '@/utils/index'
import PageButtonAdd from './pageButtonAdd'
export default {
  name: 'Permit',
  components: {
    PageButtonAdd
  },
  data() {
    return {
      list: null,
      listLoading: false,
      dialogTableVisible: false,
      currentRow: null, // 选中的右边的权限编辑项目
      currentPage: null, // 选中的左边的菜单项目
      comName: '',
      treeList: null
    }
  },
  mounted() {
    this.initTree()
    // new Promise(resolve => {
    //   this.initTree()
    //   resolve()
    // }).then(res => {
    //   this.fetchData()
    // })
  },
  methods: {
    fetchData() {
      var postData = {}
      postData.PageId = this.currentPage.id
      this.listLoading = true
      getPermit(postData).then(response => {
        const { data } = response
        this.list = data
        this.listLoading = false
      }).catch(() => {
        this.listLoading = false
      })
    },
    initTree() {
      this.listLoading = true
      GetAllList().then(response => {
        const { data } = response
        data.forEach(ele => {
          ele.label = ele.routerTitle
        })
        this.treeList = getTree(0, data)
        this.listLoading = false
      })
    },
    dialogClose() {
      this.dialogTableVisible = false
      this.fetchData()
    },
    handleAdd() {
      this.currentRow = null
      this.comName = 'PageButtonAdd'
      this.dialogTableVisible = true
    },
    handleEdit(row) {
      this.currentRow = row
      this.comName = 'PageButtonAdd'
      this.dialogTableVisible = true
    },
    handleNodeClick(data, node) {
      if (node.isLeaf) {
        // 多次点击同一个节点不请求
        if (this.currentPage) {
          if (data.id === this.currentPage.id) {
            return
          }
        }
        // console.log('data:', data)
        this.currentPage = data
        this.fetchData()
      } else {
        this.list = []
      }
    },
    handleDelete(id) {
      this.listLoading = true
      deletePermit({ id: id }).then(response => {
        this.listLoading = false
        this.$message(response.message)
        this.fetchData()
      }).catch(() => {
        this.listLoading = false
      })
    }
  }
}
