import { getPostPaging } from '@/api/rsgl/post'
import PostAdd from './postAdd'
// import PostDetail from './postDetail'
export default {
  name: 'Post',
  components: {
    PostAdd
  },
  data() {
    return {
      list: [],
      listLoading: false,
      dialogTableVisible: false,
      dialogTitle: '保存',
      postSequenceList: [],
      bussinessLineList: [],
      total: 0,
      currentPage: 1,
      pageSize: this.utils.getPersonalSettings().pageSize,
      search: {
        name: '',
        postSequenceIds: [],
        bussinessLineIds: []
      },
      currentRow: null
    }
  },
  mounted() {
    this.fetchData()
  },
  methods: {
    fetchData() {
      this.listLoading = true
      var postData = {}
      postData.name = this.search.name
      postData.currentPage = this.currentPage
      postData.pageSize = this.pageSize
      postData.postSequenceIds = this.search.postSequenceIds
      postData.bussinessLineIds = this.search.bussinessLineIds
      getPostPaging(postData).then(response => {
        const { data, count, postSequenceList, bussinessLineList } = response
        this.list = data
        this.total = count
        this.postSequenceList = postSequenceList
        this.bussinessLineList = bussinessLineList
        this.listLoading = false
      })
    },
    dialogClose() {
      this.dialogTableVisible = false
      this.fetchData()
    },
    sizeChange(val) {
      this.pageSize = val
      this.fetchData()
    },
    handleAdd() {
      this.currentRow = null
      this.dialogTableVisible = true
    },
    handleEdit(row) {
      this.currentRow = row
      this.dialogTableVisible = true
    },
    goSearch() {
      this.fetchData()
    }
  }
}
