import { addBussinessInfoReply } from '@/api/ywgl/bussinessInfo'
import UpFile from '@/components/UpLoad/UpFile'
export default {
  name: 'BussinessInfoReplyAdd',
  components: {
    UpFile
  },
  props: {
    obj: {
      type: Object,
      default: null
    }
  },
  data() {
    return {
      ruleForm: {
        id: 0,
        formId: 0,
        billId: 0,
        title: '',
        content: '',
        operation: '回复',
        fileList: [],
        attitudeType: 2,
        backGroup: 0, // 0 制单
        type: 3
      },
      contentTypeList: [{
        value: '项目现状',
        label: '项目现状'
      }, {
        value: '拓展思路',
        label: '拓展思路'
      }, {
        value: '工作计划',
        label: '工作计划'
      }, {
        value: '其他',
        label: '其他'
      }],
      isFinish: false,
      buttonLoading: false,
      refreshRouterName: '',
      rules: {
        content: [{ required: true, message: '不能为空', trigger: 'blur' }],
        title: [{ required: true, message: '不能为空', trigger: 'blur' }]
      }
    }
  },
  methods: {
    submitForm(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          var postData = JSON.parse(JSON.stringify(this.ruleForm))
          postData.formId = this.obj.id
          postData.billId = this.obj.id
          let tempIds = []
          if (postData.fileList.length) {
            tempIds = postData.fileList.map(m => {
              return { id: m.id, addFlag: m.addFlag }
            })
          }
          postData.fileList = tempIds
          this.buttonLoading = true
          addBussinessInfoReply(postData)
            .then((response) => {
              this.$message(response.message)
              this.buttonLoading = false
              this.$emit('dialogClose')
            })
            .catch(() => {
              this.buttonLoading = false
            })
        }
      })
    }
  }
}
