import { getCustomerById } from '@/api/ywgl/customer'
import UpFile from '@/components/UpLoad/UpFile'
export default {
  name: 'CustomerDetail',
  components: {
    UpFile
  },
  props: {
    id: {
      type: Number,
      default: null
    }
  },
  data() {
    return {
      ruleForm: {
        name: '',
        customerType: null,
        companyId: this.$store.state.user.selectOrgId,
        legalPerson: '',
        linkMan: '',
        linkManTel: '',
        linkManPosition: '',
        linkManChiefArea: '',
        capitalSource: '',
        capital: 0,
        companyPhone: '',
        address: '',
        email: '',
        mainBusiness: '',
        remark: '',
        inUse: true,
        upFile: [],
        fileList: []
      },
      customerTypeList: [], // 客户类别列表
      surveyList: [],
      listLoading: false,
      refreshRouterName: ''
    }
  },
  activated() {
    if (this.$route.params.id) {
      this.fetchData()
      this.refreshRouterName = this.$route.params.refreshRouterName
    }
  },
  mounted() {
  },
  methods: {
    async fetchData() {
      if (this.$route.query.id !== 0) {
        await getCustomerById({ id: this.$route.query.id }).then((response) => {
          const { data, data1, dictionaries } = response
          this.ruleForm = data
          this.surveyList = data1
          this.ruleForm.customerType = dictionaries[0].name
        })
      }
    },
    closeThePage() {
      this.utils.closeAndRefresh(this.$route.query.refreshRouterName)
    }
  }
}
