import { getPlanPaging } from '@/api/cggl/plan'
import OrgSelect2 from '@/components/OrgSelect/index2'
import SearchForm from '@/components/Other/searchForm'
// import { LSGetObject } from '@/utils/localstore'
export default {
  name: 'Plan',
  components: {
    OrgSelect2,
    SearchForm
  },
  data() {
    return {
      list: null,
      listLoading: false,
      dialogTableVisible: false,
      dialogTitle: '保存',
      total: 0,
      currentPage: 1,
      pageSize: this.utils.getPersonalSettings().pageSize,
      formState: null,
      currentRow: null,
      orgList: null,
      planTypeList: null,
      materialTypeList: null,
      search: {
        number: '',
        orgIds: [],
        projectId: null,
        states: [],
        planTypes: [],
        materialTypes: [],
        categoryName: '',
        realNumber: ''
      },
      tabPosition: 3, // 0:我发起，1：待审批 2:已审批
      rules: {}
    }
  },
  activated() {
    if (this.$route.params.refresh) {
      this.fetchData()
    }
  },
  mounted() {
    this.fetchData()
  },
  methods: {
    searchData(currentPage) {
      this.currentPage = currentPage
      this.fetchData()
    },
    fetchData() {
      this.listLoading = true
      var postData = {}
      postData.currentPage = this.currentPage
      postData.pageSize = this.pageSize
      postData.type = this.tabPosition
      postData.number = this.search.number
      postData.planTypes = this.search.planTypes
      postData.materialTypes = this.search.materialTypes
      postData.categoryName = this.search.categoryName
      postData.orgIds = this.search.orgIds
      postData.realNumber = this.search.realNumber
      // if (this.search.orgIds) {
      //   postData.orgIds = [this.search.orgIds]
      // }
      postData.states = this.search.states
      // postData.companyId = this.$store.state.user.selectOrgId

      getPlanPaging(postData).then(response => {
        const { data, count, planTypeList, materialTypeList } = response
        this.list = data
        this.total = count
        this.planTypeList = planTypeList
        this.materialTypeList = materialTypeList
        // this.formState = data.billFlow.form.formState
        this.listLoading = false
      })

      // getOrganizationList().then(response => {
      //   const { data } = response
      //   const pOrgs = data.filter(f => f.isProject)
      //   console.log('pOrgs:', pOrgs)
      //   this.orgList = pOrgs
      // })
    },
    dialogClose() {
      this.dialogTableVisible = false
      this.fetchData()
    },
    handleAdd(row) {
      this.$router.push({ name: 'PlanEdit', query: { refreshRouterName: this.$route.name }, params: { type: 3 }})
    },
    handleEdit(row) {
      this.$router.push({ name: 'PlanEdit', query: { id: row.id, refreshRouterName: this.$route.name }, params: { id: row.id }})
    },
    attitude(row) {
      this.$router.push({ name: 'PlanAttitude', query: { id: row.id, refreshRouterName: this.$route.name }, params: { id: row.id }})
    },
    getFormState(row) {
      if (!row.billFlow) { return '流程空' }
      const formState = row.billFlow.form.formState
      this.formState = formState
      const state = row.state
      if (state === -1) return '作废'
      const obj = formState.find(f => f.value === state)
      if (obj) { return obj.name } else { return '状态不详' }
    },
    sizeChange(val) {
      this.pageSize = val
      this.fetchData()
    },
    radioChange(value) {
      // 清空条件
      this.search = {
        number: '',
        orgIds: [],
        projectId: null,
        states: [],
        planTypes: [],
        materialTypes: [],
        categoryName: '',
        realNumber: ''
      }
      // 填充数据
      this.fetchData()
    },
    getPerson(bf) {
      if (bf) {
        var arr = bf.billFlowNode.filter(f => f.isCurrentState && !f.isChecked)
        return arr.map(m => m.person.name).join('、')
      }
    }
    // goDesignOrder(id) {
    //   this.$router.replace(
    //     { name: 'DesignOrder',
    //       query: { id: id, refreshRouterName: this.$route.name },
    //       params: { id: id }})
    // }
  }
}
