import { getPriceListPaging } from '@/api/cggl/priceList'
import OrgSelect2 from '@/components/OrgSelect/index2'
// import PlanFrame from './PlanFrame'
export default {
  name: 'PriceList',
  components: {
    // PlanFrame
    OrgSelect2
  },
  data() {
    return {
      list: null,
      listLoading: false,
      dialogTableVisible: false,
      dialogTitle: '保存',
      comName: 'PriceListEdit',
      total: 0,
      currentPage: 1,
      pageSize: 10,
      planStateList: null,
      planTypeList: null,
      formState: null,
      currentRow: null,
      search: {
        number: '',
        orgIds: [],
        states: []
      },
      tabPosition: 0 // 0:我发起，1：待审批 2:已审批
    }
  },
  activated() {
    if (this.$route.params.refresh) {
      this.fetchData()
    }
  },
  mounted() {
    this.fetchData()
  },
  methods: {
    fetchData() {
      this.listLoading = true

      var postData = {}
      postData.currentPage = this.currentPage
      postData.pageSize = this.pageSize
      postData.type = this.tabPosition
      postData.number = this.search.number
      postData.orgIds = this.search.orgIds
      postData.states = this.search.states
      // postData.companyId = this.$store.state.user.selectOrgId

      getPriceListPaging(postData).then(response => {
        const { data, count } = response
        this.list = data
        this.total = count
        this.listLoading = false
      })
    },
    dialogClose() {
      this.dialogTableVisible = false
      this.fetchData()
    },
    handleAdd(row) {
      this.$router.push({ name: 'PriceListEdit', query: { refreshRouterName: this.$route.name }})
    },
    handleSupplierImport(row) {
      // console.log('this.$route.name:', this.$route.name)
      this.$router.push({ name: 'PriceListSupplier', query: { id: row.id, refreshRouterName: this.$route.name, tab: 'edit' }, params: { id: row.id }})
    },
    getFormState(row) {
      if (!row.billFlow) { return '流程空' }
      const formState = row.billFlow.form.formState
      this.formState = formState
      const state = row.state
      if (state === -1) return '作废'
      const obj = formState.find(f => f.value === state)
      if (obj) { return obj.name } else { return '状态不详' }
      // const state = row.state
      // if (state === -2) {
      //   return '制单'
      // } else if (state === -1) {
      //   return '填报中'
      // } else if (state === 0) {
      //   return '提交'
      // }
    },
    radioChange(value) {
      // 清空条件
      this.search = {
        number: '',
        orgIds: [],
        states: []
      }
      // 填充数据
      this.fetchData()
    },
    sizeChange(val) {
      this.pageSize = val
      this.fetchData()
    },
    getPerson(bf) {
      if (bf) {
        var arr = bf.billFlowNode.filter(f => f.isCurrentState && !f.isChecked)
        return arr.map(m => m.person.name).join('、')
      }
    },
    handleEdit(row) {
      // console.log('this.$route.name:', this.$route.name)
      this.$router.push({ name: 'PriceListEdit', query: { id: row.id, refreshRouterName: this.$route.name, tab: 'edit' }, params: { id: row.id }})
    },
    handleImport(row) {
      this.$router.push({ name: 'PriceListSupplier', query: { id: row.id, refreshRouterName: this.$route.name, tab: 'edit' }, params: { id: row.id }})
    },
    attitude(row) {
      this.$router.push({ name: 'PriceListAttitude', query: { id: row.id, refreshRouterName: this.$route.name }, params: { id: row.id }})
    }
  }
}
