import { getPostPoolList, getPostPoolById, getPostPoolDetailById, getPostListByOrgId } from '@/api/rsgl/postPool'
import { getPersonPostEditInit } from '@/api/rsgl/person'
import { getAllUserPostById, addUserPost, putUserPost, deleteUserPost } from '@/api/rsgl/userPost'
import UserPostDetailEdit from './userPostDetailEdit'
import OrgSelect2 from '@/components/OrgSelect/index2'
export default {
  name: 'UserPostEdit',
  components: {
    UserPostDetailEdit,
    OrgSelect2
  },
  data() {
    return {
      ruleForm: {
        userId: null,
        postId: null,
        orgId: null,
        postPoolDetailId: null,
        roleId: null,
        isMain: true,
        inUse: true
      },
      keyPostPool: 0,
      listLoading: false,
      loading: false,

      dialogTableVisible: false,
      dialogTitle: '保存',
      dialogWidth: '70%',
      comName: 'UserPostDetailEdit',
      currentRow: null,
      userVal: null,

      orgList: [], // 所有的组织机构数据
      postPoolList: null, // 岗位池数据
      postList: [],
      roleList: [],
      loadOrg: false,
      userPostList: [],
      mainUserPostId: null,
      rules: {
        postPoolDetailId: [{ required: true, message: '必填', trigger: 'blur' }],
        postId: [{ required: true, message: '必填', trigger: 'blur' }],
        roleId: [{ required: true, message: '必填', trigger: 'blur' }],
        orgId: [{ required: true, message: '必填', trigger: 'blur' }]
      }
    }
  },
  watch: {

  },
  activated() {
    this.initData()
    const promise = this.getLeader()
    if (this.$route.params.id) {
      promise.then(() => {
        this.fetchData()
        this.refreshRouterName = this.$route.params.refreshRouterName
      })
    }
  },
  methods: {
    submitForm() {
      this.$refs['ruleForm'].validate(valid => {
        if (valid) {
          var newObj = JSON.parse(JSON.stringify(this.ruleForm))
          // 级联中最后一个id
          var computerpostPoolDetailId = this.ruleForm.postPoolDetailId[this.ruleForm.postPoolDetailId.length - 1]
          newObj.postPoolDetailId = computerpostPoolDetailId
          // 保存数据
          if (this.mainUserPostId) {
            this.loading = true
            putUserPost(newObj).then(response => {
              if (response.errorMsg) {
                // 验证错误
                this.$alert(`${response.errorMsg.replace(/；/g, '；<br />')}`,
                  { dangerouslyUseHTMLString: true })
                return
              }
              this.$message(response.message)
              this.loading = false
              this.closeThePage()
            }).catch(() => { this.loading = false })
          } else {
            addUserPost(newObj).then(response => {
              if (response.errorMsg) {
                // 验证错误
                this.$alert(`${response.errorMsg.replace(/；/g, '；<br />')}`,
                  { dangerouslyUseHTMLString: true })
                return
              }
              this.$message(response.message)
              this.loading = false
              this.closeThePage()
            }).catch(() => { this.loading = false })
          }
        }
      })
    },
    fetchData() {
      if (this.$route.query.id !== 0) {
        getAllUserPostById({ id: this.$route.query.id }).then((response) => {
          const { data, data2 } = response
          if (data === null) {
            this.ruleForm.userId = this.$route.query.id
            this.$message({
              message: '主岗位为空请填写！', type: 'warning' })
          } else {
            this.ruleForm = data
            this.mainUserPostId = data.id
            // 拼接岗位池id成二级数组，来匹配 选岗位池 级联菜单
            var postPoolIdArray = []
            postPoolIdArray.push(data.postPoolDetail.postPoolId)
            postPoolIdArray.push(data.postPoolDetailId)
            this.ruleForm.postPoolDetailId = postPoolIdArray
            this.keyPostPool += 1
          }
          this.userPostList = data2
        })
      }
    },
    initData() {
      getPersonPostEditInit({ companyId: 1 }).then(response => {
        const { data1, data2, data3 } = response
        this.orgList = data2
        this.postList = data1
        this.roleList = data3
        this.loadOrg = true
      })
    },
    getLeader() {
      const pro1 = new Promise((resolve, reject) => {
        getPostPoolList().then(response => {
          const { data } = response
          this.postPoolList = data.map((value, i) => {
            var child = []
            getPostPoolById({ id: value.id }).then(response => {
              const { data } = response
              data.postPoolDetail.forEach(e => {
                child.push({
                  value: e.id,
                  label: e.post.name
                })
              })
            })

            return {
              value: value.id,
              label: value.name,
              children: child
            }
          })
          this.keyPostPool += 1
          resolve()
        }).catch(() => { reject() })
      })
      return pro1
    },
    closeThePage() {
      this.utils.closeAndRefresh(this.$route.query.refreshRouterName)
    },
    dialogClose() {
      this.dialogTableVisible = false
      this.fetchData()
    },
    editUserPost(d) {
      this.userVal = this.$route.params.id
      this.currentRow = d.row
      this.dialogTitle = '编辑岗位信息'
      this.comName = 'UserPostDetailEdit'
      this.dialogTableVisible = true
    },
    addUserPost() {
      this.userVal = this.$route.params.id
      this.currentRow = null
      this.dialogTitle = '添加岗位信息'
      this.comName = 'UserPostDetailEdit'
      this.dialogTableVisible = true
    },
    delUserPost(d) {
      // 页面删除
      this.$confirm('此操作将永久删除该岗位信息, 是否继续?', '提示', {
        confirmButtonText: '确定',
        cancelButtonText: '取消',
        type: 'warning'
      }).then(() => {
        deleteUserPost({ id: d.row.id }).then(response => {
          if (response.errorMsg) {
            // 验证错误
            this.$alert(`${response.errorMsg.replace(/；/g, '；<br />')}`,
              { dangerouslyUseHTMLString: true })
            return
          }
          this.$message(response.message)
          this.loading = false
          this.fetchData()
        }).catch(() => { this.loading = false })
      }).catch(() => {
        this.$message({
          type: 'info',
          message: '已取消删除'
        })
      })
    },
    postChange(val) {
      getPostPoolDetailById({ id: val }).then(response => {
        const { data } = response
        this.ruleForm.postId = data.postId
        this.ruleForm.roleId = data.roleId
      })
    },
    orgChange(val) {
      if (val) {
        getPostListByOrgId({ id: val }).then(response => {
          const { data } = response
          this.postList = data
          console.log(data)
        })
      }
    }
  }
}
