/**
 * 改为使用mixins的方式直接将原组件混入
 * 这样既可以做到修改样式,又可以不用重复修改,当原组件修改逻辑以后本组件会同时被修改
 */
import SelectPerson from '@/components/Other/selectPerson'
import parent from '@/components/Flow/flowSelect.vue'
export default {
  components: {
    SelectPerson
  },
  mixins: [parent],
  watch: {
    billFlow() {
      const { form: { formState }, billFlowNode } = this.utils.easyDeepClone(this.billFlow)
      const newList = []
      formState.sort((a, b) => a.value - b.value)
      billFlowNode.forEach(fe => {
        fe.personId = fe.personId || null
      })
      formState.forEach((e, i) => {
        if (e.isFlowNode) {
          if (e.isCheckup) {
            const gs = this.groupBy(billFlowNode.filter(f => f.state === e.value), item => [item.group])
            e.stateNode = gs
          }
          newList.push(e)
        }
      })
      this.stateList = newList
    }
  }
}
