import { getFormById } from '@/api/ggzx/workerRegister'
import UpFile from '@/components/UpLoad/UpFile'
export default {
  name: 'WorkerRegisterDetail',
  components: {
    UpFile
  },
  data() {
    return {
      id: null,
      ruleForm: {
        id: 0,
        number: '',
        orgId: null,
        registerDate: null,
        remark: '',
        fileList: []
      },
      list: [],
      loading: false,
      orgStr: '',
      dialogTableVisible: false,
      dialogTitle: '保存',
      dialogWidth: '70%',
      comName: 'PlanDetailAdd',
      currentRow: null,
      billFlow: null,
      isLoad: false,
      attitudePara: {},
      rules: {}
    }
  },
  watch: {
  },
  activated() {
  },
  mounted() {
    this.initData()
  },
  methods: {
    async initData() {
      if (this.$route.query.id) {
        this.id = this.$route.query.id
      }
      if (this.id) {
        this.loading = true
        await getFormById({ id: this.id }).then(response => this.setData(response))
      }
    },
    setData(response) {
      console.log(response)
      const { data, orgStr } = response
      this.ruleForm = data
      this.ruleForm.fileList = data.upFile
      this.list = data.workerRegisterDetails
      this.orgStr = orgStr
      // this.billFlow = billFlowData
      // this.attitudePara = { formId: billFlowData.formId, billFlowId: data.billFlowId, planId: data.id }
      this.isLoad = true
      this.loading = false // loading 状态关闭
    },
    closeThePage() {
      this.utils.closeAndRefresh(this.$route.query.refreshRouterName)
    },
    updateFlow() {
      this.closeThePage()
    }
  }
}
