import { getDeliveryPaging } from '@/api/cggl/delivery'
import OrgSelect2 from '@/components/OrgSelect/index2'
import SearchForm from '@/components/Other/searchForm'
export default {
  name: 'Delivery',
  components: {
    OrgSelect2,
    SearchForm
  },
  data() {
    return {
      list: null,
      listLoading: false,
      dialogTableVisible: false,
      dialogTitle: '保存',
      total: 0,
      currentPage: 1,
      pageSize: this.utils.getPersonalSettings().pageSize,
      formState: [{
        value: -1,
        name: '作废'
      }, {
        value: 0,
        name: '制单'
      }, {
        value: 1,
        name: '已送货'
      }, {
        value: 2,
        name: '已入库'
      }],
      currentRow: null,
      orgList: null,
      search: {
        number: '',
        orgIds: [],
        projectId: null,
        states: [],
        // deliveryDateStartDate: null,
        // deliveryDateEndDate: null,
        deliveryDateRange: [],
        supplierIds: [],
        categoryName: ''
      },
      tabPosition: 3, // 0:我发起，1：待审批 2:已审批
      supplierList: [],
      rules: {}
    }
  },
  activated() {
    if (this.$route.params.refresh) {
      this.fetchData()
    }
  },
  mounted() {
    this.fetchData()
  },
  methods: {
    searchData(currentPage) {
      this.currentPage = currentPage
      this.fetchData()
    },
    fetchData() {
      this.listLoading = true
      var postData = {}
      postData.currentPage = this.currentPage
      postData.pageSize = this.pageSize
      // postData.type = this.tabPosition
      postData.number = this.search.number
      postData.orgIds = this.search.orgIds
      postData.states = this.search.states
      // postData.deliveryDateStartDate = this.search.deliveryDateStartDate
      // postData.deliveryDateEndDate = this.search.deliveryDateEndDate
      postData.deliveryDateRange = this.search.deliveryDateRange
      postData.supplierIds = this.search.supplierIds
      postData.categoryName = this.search.categoryName
      getDeliveryPaging(postData).then(response => {
        const { data, count, supplierList } = response
        this.list = data
        this.total = count
        this.supplierList = supplierList
        this.listLoading = false
      })
    },
    dialogClose() {
      this.dialogTableVisible = false
      this.fetchData()
    },
    handleAdd(row) {
      this.$router.push({ name: 'DeliveryEdit', query: { refreshRouterName: this.$route.name }, params: { type: 3 }})
    },
    handleEdit(row) {
      this.$router.push({ name: 'DeliveryEdit', query: { id: row.id, refreshRouterName: this.$route.name }, params: { id: row.id }})
    },
    detail(row) {
      this.$router.push({ name: 'DeliveryDetail', query: { id: row.id, refreshRouterName: this.$route.name }, params: { id: row.id }})
    },
    getFormState(row) {
      // if (!row.billFlow) { return '流程空' }
      // const formState = row.billFlow.form.formState
      // this.formState = formState
      // const state = row.state
      // if (state === -1) return '作废'
      // const obj = formState.find(f => f.value === state)
      // if (obj) { return obj.name } else { return '状态不详' }
      const state = row.state
      if (state === -1) return '作废'
      else if (state === 0) return '制单'
      else if (state === 1) return '已送货'
      else if (state === 2) return '已入库'
    },
    sizeChange(val) {
      this.pageSize = val
      this.fetchData()
    },
    radioChange(value) {
      // 清空条件
      this.search = {
        number: '',
        orgIds: [],
        projectId: null,
        states: [],
        // deliveryDateStartDate: null,
        // deliveryDateEndDate: null,
        deliveryDateRange: [],
        supplierIds: [],
        categoryName: ''
      }
      // 填充数据
      this.fetchData()
    },
    getPerson(bf) {
      if (bf) {
        var arr = bf.billFlowNode.filter(f => f.isCurrentState && !f.isChecked)
        return arr.map(m => m.person.name).join('、')
      }
    }
  }
}
