import { getTechnicalBidEditInit, getTechnicalBidById } from '@/api/ywgl/technicalBid'
import UpFile from '@/components/UpLoad/UpFile'
import FlowDisplay from '@/components/Flow/flowDisplay'
import FlowAttitude from '@/components/Flow/flowAttitude'
export default {
  name: 'ProjectSiteSignEdit',
  components: {
    FlowDisplay,
    FlowAttitude,
    UpFile
  },
  data() {
    return {
      ruleForm: {
        id: 0,
        number: '',
        orgId: null,
        companyId: this.$store.state.user.selectOrgId,
        name: '',
        remark: '',
        state: 0,
        billFlowId: 0,
        fileList: []
      },
      siteSignId: null,
      projectName: '',

      keyOrg: 0,
      orgList: [],
      coordinateTypeList: [],
      progressList: [],

      attitudePara: null,
      loadFlow: false,
      billFlow: null,
      isCheck: false,

      active: 0,
      loading: false,
      rules: {
      }
    }
  },
  activated() {
    if (this.$route.params.id) {
      this.ruleForm.orgId = null // 刷新 org 列表
      this.initData()
      this.refreshRouterName = this.$route.params.refreshRouterName
    }
  },
  mounted() {
    if (!this.$route.params.id) {
      this.initData()
      this.activate = true
    }
  },
  methods: {
    initData() {
      // 有参数id 就是编辑
      if (this.$route.query.id) {
        this.siteSignId = this.$route.query.id
      }
      new Promise((resolve, reject) => {
        getTechnicalBidEditInit().then(response => {
          const { orgList, result1, result4 } = response
          this.orgList = orgList
          this.coordinateTypeList = result1
          this.progressList = result4
          this.keyOrg += 1
          resolve(orgList)
        })
      }).then(orgList => {
        if (this.siteSignId) {
          getTechnicalBidById({ id: this.siteSignId }).then(response => {
            const { data } = response
            this.ruleForm = data
            this.keyOrg += 1
            this.ruleForm.fileList = data.upFile
          })
        }
      }).catch(() => {
        this.$message.error('信息错误')
      })
    },
    updateFlow() {},
    closeThePage() {
      this.utils.closeAndRefresh(this.$route.query.refreshRouterName)
    }
  }
}
