import { getWorkerPaging, getWorkerList } from '@/api/grgl/worker'
import OrgSelect2 from '@/components/OrgSelect/index2'
import SearchForm from '@/components/Other/searchForm'

export default {
  name: 'WorkerSelect',
  components: {
    OrgSelect2,
    SearchForm
  },
  props: {
    dialogWidth: { type: String, default: '100%' }, // 弹出层的宽度
    multiple: { type: Boolean, default: true }, // 默认多选
    type: { type: Number, default: null },
    workerList: { type: Array, default: () => [] }, // 默认选中ID组
    orgId: { type: Number, default: null }, // 默认选中项目部ID
    year: { type: Number, default: new Date().getFullYear() }, // 年份-默认当年
    bdState: { type: Boolean, default: null }, // 是否报到
    ispause: { type: Boolean, default: null },
    isexit: { type: Boolean, default: null }
  },
  data() {
    return {
      open: false,
      currentPage: 1,
      pageSize: this.utils.getPersonalSettings().pageSize,
      total: 0,
      search: {
        year: 0,
        orgIds: [],
        number: '',
        name: '',
        bdState: null,
        ispause: null,
        isexit: null
      },
      disabled: false,
      buttonLoading: false,
      selectionItem: [],
      tableList: [],
      yearList: [],
      tableLoading: null
    }
  },
  activated() {
  },
  mounted() {
    this.fetchData()
  },
  methods: {
    openDialog() {
      // 清空数据
      this.selectionItem = []
      this.currentPage = 1
      this.pageSize = 10
      if (this.orgId) {
        this.search.orgIds.push(this.orgId)
      }
      this.search.number = ''
      this.search.name = ''
      this.search.year = this.year
      this.search.bdState = this.bdState
      this.search.ispause = this.ispause
      this.search.isexit = this.isexit
      this.open = true
      if (this.multiple === false) {
        this.disabled = true
      }
      this.goSearch()
    },
    fetchData() {
      this.goSearch()
    },
    goSearch() {
      const postData = {}
      postData.currentPage = this.currentPage
      postData.pageSize = this.pageSize
      postData.year = this.search.year
      postData.orgIds = this.search.orgIds
      postData.number = this.search.number
      postData.name = this.search.name
      postData.bdState = this.search.bdState
      postData.pauseState = this.search.ispause
      postData.exitState = this.search.isexit
      this.buttonLoading = true
      setTimeout(() => {
        this.getPersonList(postData)
      }, 700)
    },
    getPersonList(postData) {
      getWorkerPaging(postData).then(res => {
        const { data, yearOptions, count } = res
        this.tableList = data
        // this.workerList.forEach(wor => {
        //   this.tableList.forEach(element => {
        //     if (wor.id === element.id) {
        //       this.selectionItem.push(element)
        //     }
        //   })
        // })
        this.buttonLoading = false
        this.yearList = yearOptions
        this.total = count
      }).catch(() => { this.buttonLoading = false })
      // 已选择项
      const workerIds = []
      this.workerList.forEach(wor => {
        workerIds.push(wor.id)
      })
      if (workerIds.length > 0) {
        getWorkerList({ checkIds: workerIds }).then(res => {
          const { data } = res
          this.selectionItem = data
        }).catch()
      }
    },
    add(row) {
      if (this.multiple === false) {
        this.selectionItem.push(row)
        this.save()
      } else {
        this.selectionItem.push(row)
      }
    },
    save() {
      this.buttonLoading = true
      // 触发父组件的事件
      setTimeout(() => {
        this.$emit('getPersons', this.selectionItem)
        this.buttonLoading = false
        this.open = false
      }, 300)
    },
    /**
     * @description: 设置分页大小
     * @param {Number} val
     */
    sizeChange(val) {
      this.pageSize = val
      this.goSearch()
    },
    cancel(row) {
      if (this.multiple === true) {
        this.selectionItem.splice(row)
      }
    }
  }
}
