/**
 * @props
 * status: 控制组件显示详情
 * infoList: 显示详情的数据
 * labelWidth: label的宽度
 */
import UpFile from '@/components/UpLoad/UpFile'
export default {
  name: 'Detailsedit',
  components: {
    UpFile
  },
  props: {
    status: {
      type: String,
      default: ''
    },
    infoList: {
      type: Array,
      default() {
        return []
      }
    },
    labelWidth: {
      type: Number,
      default: 100
    }
  },
  computed: {
    /**
     * 计算表单结构
     */
    detailList() {
      const { infoList } = this
      const newList = []
      for (const value of infoList) {
        const length = newList.length
        const item = {
          ...value,
          component: value.component || ''
        }
        if (length > 0 && newList[ length - 1].length < 2 && !newList[ length - 1][0].monopolize && !value.monopolize) {
          // 往上一行添加一个
          newList[ length - 1].push(item)
        } else {
          newList.push([item])
        }
      }
      return newList
    }
  },
  mounted() {},
  methods: {
    infoItemClick(item, ...args) {
      typeof item.click === 'function' && item.click(...args)
    }
  }
}
