import { GetPaymentById } from '@/api/cwgl/zjgl/zjsk.js'
export default {
  name: 'ShouKuanDetails',
  data() {
    return {
      table: {
        list: [],
        loading: false // 获取数据状态
      },

      infoList: [ // 遍历的数据 label:展示名   value:展示值  monopolize:独占一行   name:对应response返回的属性名
        { label: '收款编号', value: '', name: 'number' },
        { label: '部门', value: '', name: 'orgName' },
        { label: '收款类型', value: '', name: 'gatheringTypeName' },
        { label: '资金形式', value: '', name: 'capitalTypeName' },
        { label: '收款日期', value: '', name: 'gatheringDate' },
        { label: '经手人', value: '', name: 'personName' },
        { label: '金额', value: '', name: 'amount' },
        { label: '收款银行', value: '', name: 'bank' },
        { label: '备注', value: '', name: 'remark', monopolize: true },
        { label: '制单人', value: '', name: 'createUserName' },
        { label: '制单时间', value: '', name: 'createDate' },
        { label: '更新人', value: '', name: 'lastEditUserName' },
        { label: '更新时间', value: '', name: 'lastEditDate' }
      ]
    }
  },
  created() {
    this.fetchData()
  },
  methods: {
    async fetchData() {
      this.table.loading = true
      try {
        // To do
        const res = await GetPaymentById(this.$route.params.id)
        this.table.list = res.data.acceptanceList
        this.infoList.forEach((item) => {
          item.value = res.data[item.name] || ''
        })
      } catch (e) {
        console.log('fetchData:请求列表数据失败，错误信息：', e)
      } finally {
        this.table.loading = false
      }
    }
  }
}
