import { putBussinessInfoBid, addBussinessInfoReply } from '@/api/ywgl/bussinessInfo'
import UpFile from '@/components/UpLoad/UpFile'
export default {
  name: 'BussinessInfoSummary',
  components: {
    UpFile
  },
  props: {
    obj: {
      type: Object,
      default: null
    }
  },
  data() {
    return {
      summaryForm: {
        id: 0,
        formId: 0,
        billId: 0,
        title: '投标总结',
        content: '',
        operation: '投标总结',
        fileList: [],
        attitudeType: 2,
        backGroup: 0, // 0 制单
        type: 5
      },
      buttonLoading: false,
      refreshRouterName: '',
      rules: {

      }
    }
  },
  mounted() {
    this.initData()
  },
  activated() {
    this.initData()
  },
  methods: {
    initData() {
      // eslint-disable-next-line no-empty
      if (this.obj) {}
    },
    submitForm(formName) {
      this.$refs['ruleForm'].validate((valid) => {
        if (valid) {
          this.buttonLoading = false
          var postData = JSON.parse(JSON.stringify(this.ruleForm))
          putBussinessInfoBid(postData)
            .then((response) => {
              if (response.code === 200) {
                this.addSummary(response.data)
              }
              this.buttonLoading = false
            })
            .catch(() => {
              this.buttonLoading = false
            })
        }
      })
    },
    addSummary(data) {
      this.$refs['summaryForm'].validate((valid) => {
        if (valid) {
          var attitudeData = JSON.parse(JSON.stringify(this.summaryForm))
          attitudeData.formId = this.billFlow.formId
          attitudeData.billId = this.billFlow.billId
          attitudeData.content = attitudeData.content + data
          let tempIds = []
          if (attitudeData.fileList.length) {
            tempIds = attitudeData.fileList.map(m => {
              return { id: m.id, addFlag: m.addFlag }
            })
          }
          attitudeData.fileList = tempIds
          addBussinessInfoReply(attitudeData)
            .then((response) => {
              this.$message(response.message)
              this.closeThePage()
            })
            .catch(() => {
              this.buttonLoading = false
            })
        }
      })
    }
  }
}
