import { getPriceSupplierList, getPriceListInfo, priceSupplierImport } from '@/api/cggl/priceList'
import { getDictionarysByTypes } from '@/api/xtgl/dictionary'
import OrgSelect2 from '@/components/OrgSelect/index2'
import { validate } from '@/utils/validate'
export default {
  name: 'PriceListSupplier',
  components: {
    OrgSelect2
  },
  data() {
    return {
      supplierForm: {
        supplierId: null
      },
      priceListSuppliers: [],
      ruleForm: {},
      loading: false,
      showbuttons: false,
      // 附件页面
      dialogTableVisible: false,
      dialogTitle: '保存',
      dialogWidth: '70%',
      currentRow: null,
      // 材料明细
      list: [],
      // 付款方式明细
      paymodel: {
        rules: {
          requirement: [{ required: true, message: '请输入', trigger: 'blur' }],
          time: [{ validator: validate.logic, logic: value => value > 0, required: true, trigger: 'blur' }],
          unit: [{ required: true, message: '请输入', trigger: 'blur' }],
          scale: [{ validator: validate.logic, logic: value => value > 0, required: true, trigger: 'blur' }]
        },
        list: []
      },
      fileList: [],
      // 供应商
      suppliers: [],
      // 发票种类
      receiptTypes: [],
      keyreceiptType: 0,
      // 卸车费归属
      otherPricePays: [],
      keyotherPricePay: 0,
      rules: {},
      supplierRule: {},
      active: 0,
      disabled: false,
      columnShow: { unit2: true,
        brand: true,
        model: true,
        spec: true,
        thickness: true,
        texture: true,
        surfaceTreatment: true,
        designNumber: true
      }
    }
  },
  computed: {
    getOrgList() {
      return this.$store.state.user.info.orgList
    }
  },
  watch: {},
  activated() {
    this.initData()
  },
  mounted() {
  },
  methods: {
    saveForm() {
    },
    submitForm(formName, state) {
      this.$refs[formName].validate(valid => {
        if (valid) {
          this.$refs['paymodel'].validate(val => {
            if (val) {
              var postData = {}
              var newObj = JSON.parse(JSON.stringify(this.ruleForm))
              // 拼接材料明细参数
              var arr1 = []
              this.list.forEach(e => {
                var item = {}
                item.id = e.id
                item.priceSupplierId = this.supplierForm.supplierId
                item.priceListDetailId = e.priceListDetailId
                item.state = state
                item.planDetailId = e.id
                item.price = e.price
                item.amount = e.amount
                item.remark = e.remark
                item.materialId = e.materialId
                arr1.push(item)
              })
              // 拼接付款方式参数
              var arr2 = []
              this.paymodel.list.forEach(e => {
                var item2 = {}
                item2.id = e.id
                item2.priceListId = newObj.priceListId
                item2.requirement = e.requirement
                item2.time = e.time
                item2.unit = e.unit
                item2.scale = e.scale
                item2.remark = e.remark
                arr2.push(item2)
              })

              postData.id = newObj.id
              postData.brand = newObj.brand
              postData.standardData = newObj.standardData
              postData.receiptType = newObj.receiptType
              postData.payStatus = newObj.payStatus
              postData.otherPricePay = newObj.otherPricePay
              postData.amountContain = newObj.amountContain
              postData.supplyData = newObj.supplyData
              postData.explain = newObj.explain
              postData.contacts = newObj.contacts
              postData.telNumber = newObj.telNumber
              postData.remark = newObj.remark
              postData.state = state
              postData.priceListId = newObj.priceListId
              postData.supplierId = newObj.supplierId
              postData.priceSupplierDetails = arr1
              postData.priceListPayStatuses = arr2

              this.loading = true
              priceSupplierImport(postData).then(response => {
                this.$message(response.message)
                this.loading = false
                this.closeThePage()
              }).catch(() => { this.loading = false })
            }
          })
        }
      })
    },
    async initData() {
      // 供应商列表 priceListId
      getPriceSupplierList({ id: this.$route.query.id }).then((response) => {
        this.priceListSuppliers = response.data
      })
      getDictionarysByTypes({ types: 'receiptTypes,otherPricePays' }).then((response) => {
        const { data } = response
        data.forEach(element => {
          element.pid = element.pId
          if (element.type === 'receiptTypes') {
            this.receiptTypes.push(element)
          }
          if (element.type === 'otherPricePays') {
            this.otherPricePays.push(element)
          }
        })
        this.keyreceiptType += 1
        this.keyotherPricePay += 1
      })
    },
    setSupplierId() {
      const supplierId = this.supplierForm.supplierId
      if (!supplierId) {
        this.$alert('请选择填报的供应商！')
      } else {
        // 报价单 id,supplierId
        getPriceListInfo({ id: this.$route.query.id, supplierId: supplierId }).then((response) => {
          this.ruleForm = response.suppliers
          // this.list = response.detailList
          this.setData(response)
          this.active = 1
          this.keyreceiptType += 1
          this.keyotherPricePay += 1
        })
        this.disabled = true
      }
    },
    closeThePage() {
      this.utils.closeAndRefresh(this.$route.query.refreshRouterName)
    },
    dialogClose(data, state) {
      this.dialogTableVisible = false
    },
    showFileList(fileList) {
      this.dialogTitle = '附件列表'
      this.comName = 'UpFile'
      this.dialogWidth = '50%'
      this.fileList = fileList
      this.dialogTableVisible = true
    },
    previous() {
      this.active = 0
      this.disabled = false
    },
    setData(response) {
      const { detailList, paystatus } = response
      var pom = []
      detailList.forEach(e => {
        pom.push({
          id: e.id,
          categoryId: e.material.categoryId,
          category: { id: e.material.categoryId, name: e.material.materialCategory.name, unit: e.material.materialCategory.unit },
          unit2: e.material.unit2,
          brand: e.material.brand,
          model: e.material.model,
          spec: e.material.spec,
          thickness: e.material.thickness,
          texture: e.material.texture,
          surfaceTreatment: e.material.surfaceTreatment,
          designNumber: e.material.designNumber,
          applicationQuantity: e.priceListDetail.planDetail.applicationQuantity,
          applicationQuantity2: e.priceListDetail.planDetail.applicationQuantity2,
          quantity: e.priceListDetail.planDetail.quantity,
          quantity2: e.priceListDetail.planDetail.quantity2,
          entryDate: e.priceListDetail.planDetail.entryDate,
          remark: e.remark,
          materialId: e.materialId,
          priceListDetailId: e.priceListDetailId
          // fileList: detailList.upFile.filter(f => f.tableId === e.id)
        })
      })
      // 填充 明细列表
      this.list = pom
      var pom2 = []
      paystatus.forEach(e => {
        pom2.push({
          id: e.id,
          requirement: e.requirement,
          time: e.time,
          unit: e.unit,
          scale: e.scale,
          remark: e.remark
        })
      })
      this.paymodel.list = pom2
      this.setColumnShow()
    },
    setColumnShow() { // 设置列的显示。列中，都没有值，就隐藏
      var modelunit2 = false
      var modelbrand = false
      var modelmodel = false
      var modelspec = false
      var modelthickness = false
      var modeltexture = false
      var modelsurfaceTreatment = false
      var modeldesignNumber = false
      for (var row of this.list) {
        if (row.unit2) { modelunit2 = true }
        if (row.brand) { modelbrand = true }
        if (row.model) { modelmodel = true }
        if (row.spec) { modelspec = true }
        if (row.thickness) { modelthickness = true }
        if (row.texture) { modeltexture = true }
        if (row.surfaceTreatment) { modelsurfaceTreatment = true }
        if (row.designNumber) { modeldesignNumber = true }
      }
      this.columnShow['unit2'] = modelunit2
      this.columnShow['brand'] = modelbrand
      this.columnShow['model'] = modelmodel
      this.columnShow['spec'] = modelspec
      this.columnShow['thickness'] = modelthickness
      this.columnShow['texture'] = modeltexture
      this.columnShow['surfaceTreatment'] = modelsurfaceTreatment
      this.columnShow['designNumber'] = modeldesignNumber
    },
    add() {
      var data = {
        requirement: '',
        time: '',
        unit: '',
        scale: '',
        remark: ''
      }
      this.paymodel.list.push(data)
    },
    del(d) {
      // 列表删除
      this.paymodel.list.splice(d.$index, 1)
    }
  }
}
