import { getAllUserPostById } from '@/api/rsgl/userPost'
export default {
  name: 'UserPostDetail',
  components: {

  },
  data() {
    return {
      ruleForm: {
        id: 0,
        postId: null,
        post: {
          name: ''
        },
        orgId: null,
        organization: {
          name: ''
        },
        postPoolDetailId: null,
        postPoolDetail: {
          postPool: {
            name: ''
          }
        },
        roleId: null,
        role: {
          name: ''
        },
        isMain: true,
        inUse: true
      },
      listLoading: false,
      userPostList: []
    }
  },
  watch: {

  },
  activated() {
    if (this.$route.params.id) {
      this.fetchData()
      this.refreshRouterName = this.$route.params.refreshRouterName
    }
  },
  methods: {
    async fetchData() {
      if (this.$route.query.id !== 0) {
        await getAllUserPostById({ id: this.$route.query.id }).then((response) => {
          const { data, data2 } = response
          if (data !== null) {
            this.ruleForm = data
            this.mainUserPostId = data.id
          }
          this.userPostList = data2
        })
      }
    },
    closeThePage() {
      this.utils.closeAndRefresh(this.$route.query.refreshRouterName)
    }
  }
}
